function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	//가원산업개발, 대륙산업개발 일 경우 40번 권한으로
	//if( G_VEND_CODE_E == '6212132828' || G_VEND_CODE_E == '2062376067' )
	//{
	//	userGroup = '40';
	//}


	if( userGroup == '10' )
	{
		chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;
	}
	else
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;
	}

	var codeArr = ["PS003"];
	var dsObjArr = [ds_zzactss];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);


	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0204007A_S2");
	tit_addSearchActionInfo("ps02:PS0201001A_S34");	
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_pm=ds_pm ds_lifnr=ds_zzlifnr_cd"
		, ""
		, "fn_afterZzLifnr"
		, true,true);

	ed_pspid.SetFocus();

	//cal_datef.Value = gfn_getCurrDate()  ;	
	cal_datef.Value = gfn_getBeforeDate( gfn_getCurrDate() , 150 );
	cal_datet.Value = '99991231';
	gfn_showMsg("CI00007");

	//cb_gbn.Index = 0;
	rd_chk0.index = 0;
	rd_chk1.index = 0;
	cb_gbn2.Index = 0;
//alert("7");
	ufn_visible('1');

	grd_list.Format = "Mbefore";
	grd_list.Redraw();
	grd_list.BindDataset = "ds_list";
	//grd_list.Format = "Mafter";

}
