/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	// 필수 입력사항 및 일자 체크
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) ) {
		return;
	}
	//설치협력사 조회시 협력업체 필수체크
	if(userGroup == '10' && gfn_isNull(cb_zzlifnr, true, st_zzlifnr.value)) {
		return;
	}
	//사무소 조회시 사무소 필수체크
	if(userGroup == '30' && gfn_isNull(cb_zzactss, true, st_zzactss.value)) {
		return;
	}
	if(cal_datef.Value == '' ){
		//프로젝트필드 필수체크
		if(gfn_isNull(ed_pspid, true, st_pspid.value))
			return;
	}

	if (rd_chk0.index == 0)  //자재출하전 
	{
/*	
		if (rd_chk1.Index == 0)
		{
			if(chk_pm1.value != '1' && chk_pm2.value != '1')
			{
				gfn_showMsg("CW00207","미처리 구분값");
				return;
			}	
		}
		else if (rd_chk1.Index == 1)
		{
			if(chk_mt1.value != '1' && chk_mt2.value != '1' && chk_mt3.value != '1' && chk_mt4.value != '1' && chk_mt5.value != '1')
			{
				gfn_showMsg("CW00207","미처리 구분값");
				return;
			}		
		}
*/	

		ufn_sql();	


//alert("1");		
//fn_createWait("조회중", "PS0204008A");
//alert("2");
		tit_clearActionInfo();		
		tit_addCsvSearchActionInfo("ps02:PS0204008A_S1");  		
		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, args 
			, "fn_afterQuery"
			, ""
			, ""
			, true
		);	
//alert("3");		
//SetWaitCursor(false);
//ReleaseCapture();

//fn_destroyWait();
//alert("4");		

	}
	else if (rd_chk0.index == 1)  //자재출하후
	{
		tit_clearActionInfo();		
		tit_addCsvSearchActionInfo("ps02:PS0204008A_S2");  		
		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list2=ds_list2"
			, ""   //args 
			, "fn_afterQuery"
			, ""
			, ""
			, true
		);	


	}
	else if (rd_chk0.index == 2)  //전체
	{

		tit_clearActionInfo();		
		tit_addCsvSearchActionInfo("ps02:PS0204008A_S3");  		
		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list3=ds_list3"
			, ""   //args 
			, "fn_afterQuery"
			, ""
			, ""
			, true
		);		

	}


}
