		gfn_selectGridCol("grd_list",true);
	} else {
//alert("obj : " + obj);
//alert("nCell : " + nCell);	
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 사업부 선택시 ds_lifnr 데이터셋 값 filtering
********************************************************************************/
function chk_so2_OnClick(obj,strValue)
{

	var so = ' ';
	var so2 = ' ';
	var so3 = ' ';
	var so4 = ' ';
	var so5 = ' ';
	var so7 = ' ';

	if(chk_so.Value == '1')  so =  'X';
	if(chk_so2.Value == '1') so2 = 'X';
	if(chk_so3.Value == '1') so3 = 'X';
	if(chk_so4.Value == '1') so4 = 'X';
	if(chk_so5.Value == '1') so5 = 'X';
	if(chk_so7.Value == '1') so7 = 'X';


	if(chk_so.Value+chk_so2.Value+chk_so3.Value+chk_so4.Value+chk_so5.Value+chk_so7.Value == '000000')
	{
		ds_lifnr.Filter("SO5=='' || CODE==''");
	}	
	else
	{
	  ds_lifnr.Filter("CODE=='' || SO=="+quote(so) +" || SO2=="+quote(so2) +" || SO3=="+quote(so3) +" || SO4=="+quote(so4) +" || SO5=="+quote(so5) +" || SO7=="+quote(so7));
	}

	cb_zzlifnr.Index = 0;	
}



/********************************************************************************
* 기      능   : 자재출하 전/후 값 변경시
********************************************************************************/
function rd_chk0_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
//alert("8");
	if (rd_chk0.Index != 0 )
	{
		ufn_visible('a');

		if (rd_chk0.Index == 1)     
		{
			ds_list2.Clear();
			gfn_showMsg("CI00007");
			cal_datef.Value = gfn_getBeforeDate( gfn_getCurrDate() , 60 );
			cal_datet.Value = gfn_getafterDate( gfn_getCurrDate() , 60 );			

            grd_list.BindDataset = "ds_list2";   
            grd_list.SetFormat("Mafter");   
            grd_list.Redraw();

		}
		else if (rd_chk0.Index == 2)     
		{	
			ds_list3.Clear();
			gfn_showMsg("CI00007");
			cal_datef.Value = gfn_getBeforeDate( gfn_getCurrDate() , 60 );
			cal_datet.Value = gfn_getafterDate( gfn_getCurrDate() , 60 );

			grd_list.BindDataset = "ds_list3";		
			grd_list.SetFormat("Mall");  
            grd_list.Redraw();                       

		}


	}
	else
	{
		if (rd_chk1.index == 0) 		ufn_visible('1');
		else if (rd_chk1.index == 1)	ufn_visible('2');

		ds_list.Clear();
		gfn_showMsg("CI00007");
		cal_datef.Value = gfn_getBeforeDate( gfn_getCurrDate() , 150 );
		cal_datet.Value = '99991231';

		grd_list.BindDataset = "ds_list";        
        grd_list.SetFormat("Mbefore");   
        grd_list.Redraw();       


	}	

	//grd_list.CreateFormat();
	//gfn_initForm(true);



}



