/********************************************************************************
* 기      능   : 미처리 구분에 따른 체크박스 활성화
********************************************************************************/
function ufn_visible(visck) {
	if ( visck == 'a' )
	{
		Static3.Visible = false;	Static1.Visible = false;	rd_chk1.Visible = false;	
		Static7.Visible = false;	chk_pm1.Visible = false;	chk_pm2.Visible = false;	
		Static6.Visible = false;	chk_mt1.Visible = false;	chk_mt2.Visible = false;	chk_mt3.Visible = false;
		                       	    chk_mt4.Visible = false;	chk_mt5.Visible = false;
	}
	else if ( visck == '1' )
	{
		Static3.Visible = true;		Static1.Visible = true;		rd_chk1.Visible = true;	
		Static7.Visible = true;		chk_pm1.Visible = true;		chk_pm2.Visible = true;	
		Static6.Visible = false;	chk_mt1.Visible = false;	chk_mt2.Visible = false;	chk_mt3.Visible = false;
		                       	    chk_mt4.Visible = false;	chk_mt5.Visible = false;
	}
	else if ( visck == '2' )
	{
		Static3.Visible = true;		Static1.Visible = true;		rd_chk1.Visible = true;	
		Static7.Visible = false;	chk_pm1.Visible = false;	chk_pm2.Visible = false;	
		Static6.Visible = true;		chk_mt1.Visible = true;		chk_mt2.Visible = true;		chk_mt3.Visible = true;
		                       	    chk_mt4.Visible = true;		chk_mt5.Visible = true;
	}	

}


/********************************************************************************
* 기      능   : 미처리 구분 체크박스에 따른 SQL  OR 조건 생성
********************************************************************************/
function ufn_sql() {
	args = "";
	var q_pm = Array(2);	
	var q_mt = Array(5);
	var q = "";


	if ( rd_chk1.Index == 0 )
	{	
		if(chk_pm1.value != '1' && chk_pm2.value != '1') return;

		if (chk_pm1.Value == '1')   		q_pm[0] = "T1.STON_DATE < '10000101' ";		
		if (chk_pm2.Value == '1')   		q_pm[1] = "T1.ZZINTER = '' "; 

		for(var i=0; i<2; i++)
		{
			if (i==0 )  
			{ 
				q += "  AND (" + q_pm[i];
			}
			else
			{
				//if ( length(q_pm[i-1]) > 0 && length(q_pm[i]) > 0)
				if ( trim(q) != "AND (" && length(q_pm[i]) > 0)
				{				
					q += "OR " + q_pm[i];
				}
				else
				{
					q += q_pm[i];
				}							
			}

			if (i==1) 
			{
				q += " )";
			}
		}	
	}
	else if ( rd_chk1.Index == 1 )
	{		
		if(chk_mt1.value != '1' && chk_mt2.value != '1' && chk_mt3.value != '1' && chk_mt4.value != '1' && chk_mt5.value != '1')  return;

		if (chk_mt1.Value == '1')   q_mt[0] = "T1.SURVEY_DAY_O = '00000000' " ;	 
		if (chk_mt2.Value == '1')   q_mt[1] = "T1.HOOKDAY < '10000101' ";	 
		if (chk_mt3.Value == '1')   q_mt[2] = "(T1.REQ_DAT = '00000000' AND T1.H_VALUE = 'Y') ";	 
		if (chk_mt4.Value == '1')   q_mt[3] = "T1.LIFT_TMNM = '' "      ;	 
		if (chk_mt5.Value == '1')   q_mt[4] = "T1.GETOFF_DAT < '10000101' ";	

		for(var i=0; i<5; i++)
		{
			if (i==0 )  
			{ 
				q += "  AND (" + q_mt[i];
			}
			else
			{
				//if ( length(q_mt[i-1]) > 0 && length(q_mt[i]) > 0)
				if ( trim(q) != "AND (" && length(q_mt[i]) > 0)
				{				
					q += "OR " + q_mt[i];
				}
				else
				{
					q += q_mt[i];
				}							
			}

			if (i==4) 
			{
				q += " )";
			}
		}		

	}

	args += "S_QUERY="+quote(q);


}




