function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	var arg = "";

	if ( grd_list.Format == 'Mbefore')
	{

		switch (nCell) 
		{
			case   3 : 	gfn_openPopup("PS0204008B",true);
						break;

			case   6 :	gfn_openPopup("PS0204008L", true );
						break;					

			case  10 :	gfn_openPopup("PS0204008C", true );
						break;

			case  13 :	gfn_openPopup("PS0204008D", true );
						break;

			//case  14 :	gfn_openPopup("PS0204008E", true );        -- 최근이력 필드 삭제 (2018.09.11 고범석 과장님 요청)
			//			break;

			case  15 :  arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID")) +
							  " s_inter="+quote(ds_list.GetColumn(nRow, "ZZINTER")) ;
						//gfn_openWin("PS0201001Z",arg);  //  모바일 적용후 PS0201001A 로 변경
						gfn_openWin("PS0201001A",arg);  
						break;

			case  17 :  arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID")) +
							  " s_inter="+quote(ds_list.GetColumn(nRow, "ZZINTER")) ;
//						gfn_openWin("PS0201001Z",arg);  //  모바일 적용후 PS0201001A 로 변경
						gfn_openWin("PS0201001A",arg);  
						break;		


			case  18 : if ( ds_list.GetColumn(nRow, "SURVEY_DAY") == "입력" )
						{
							arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID")) +
							      " s_hno="+quote(substr(ds_list.GetColumn(nRow, "POSID"),6,3)) ;
							if ( ds_list.GetColumn(nRow, "ZZGUBUN") == '10' )  gfn_openWin("PS1107001A",arg);
							if ( ds_list.GetColumn(nRow, "ZZGUBUN") == '13' )  gfn_openWin("PS1107001A",arg);
							if ( ds_list.GetColumn(nRow, "ZZGUBUN") == '11' )  gfn_openWin("PS1109001A",arg);
							if ( ds_list.GetColumn(nRow, "ZZGUBUN") == '14' )  gfn_openWin("PS1109001A",arg);
							if ( ds_list.GetColumn(nRow, "ZZGUBUN") == '12' )  gfn_openWin("PS1110001A",arg);
						}
						else
						{
							arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID")) +
							      " s_hno="+quote(substr(ds_list.GetColumn(nRow, "POSID"),6,3)) ;

							if ( ds_list.GetColumn(nRow, "ZZGUBUN") == '10' )  gfn_openPopup("PS1107001B", true, arg );
							if ( ds_list.GetColumn(nRow, "ZZGUBUN") == '13' )  gfn_openPopup("PS1107001B", true, arg );
							if ( ds_list.GetColumn(nRow, "ZZGUBUN") == '11' )  gfn_openPopup("PS1109001B", true, arg );
							if ( ds_list.GetColumn(nRow, "ZZGUBUN") == '14' )  gfn_openPopup("PS1109001B", true, arg );
							if ( ds_list.GetColumn(nRow, "ZZGUBUN") == '12' )  gfn_openPopup("PS1110001B", true, arg );

						}
						break;	

	//======> 여기서 부터
			case  19 :  if( ds_list.GetColumn(nRow, "HOOKDAY") == "입력" )
			            {
							arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID"))  ;
							gfn_openWin("PS1104005A",arg);
							break;			
						}
						else
						{
							arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID"))  ;
							gfn_openWin("PS1104006A",arg);
							break;	
						}

			case  20 :	gfn_openPopup("PS0204008H", true );
						break;		


			case  21 :	gfn_openPopup("PS0204008I", true );
						break;			

			case  23 :	gfn_openPopup("PS0204008J", true );
						break;						
		}

	}
	else if ( grd_list.Format == 'Mafter')
	{

		switch (nCell) 
		{
			case   3 : 	gfn_openPopup("PS0204008B",true);							
						break;

			case   7 :	gfn_openPopup("PS0204008L", true );
						break;							

			case  13 :	gfn_openPopup("PS0204008D", true );
						break;			

			case  14 :	gfn_openPopup("PS0204008K", true );
						break;							

			case  23 :	gfn_openPopup("PS0204008E", true );
						break;			
		}

	}
	else if ( grd_list.Format == 'Mall')
	{

		switch (nCell) 
		{
			case   3 : 	gfn_openPopup("PS0204008B",true);							
						break;

			case  11 :	gfn_openPopup("PS0204008L", true );
						break;							

		}	

	}



}
