var user_grp = "";
var F_MENU = "";
var fv_gbn = "";
var PUID = "";
var PUSER = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm();

	user_grp = gfn_getUserGroup();

	ds_cond.fireevent = false;
	//오늘 +1일
	//현재월+3개월의 마지막날 gfn_getLeapLastDay(date)
	/*
	trace("오늘+1일 : "+gfn_getAfterDate(gfn_getCurrDate(),1));
	trace("현재월 : "+gfn_getCurrMonth()); // 201810
	trace("현재월 +3개월 : "+gfn_getAfterMonth(gfn_getCurrMonth(),3));
	trace("현재월+3개월의 마지막날 : "+gfn_getLeapLastDay(gfn_getAfterMonth(gfn_getCurrMonth(),3)+'01'));
	*/



	ds_cond.SetColumn(0, "I_FDATE", gfn_getAfterDate(gfn_getCurrDate(),1));	//오늘 + 1일
	ds_cond.SetColumn(0, "I_TDATE", gfn_getLeapLastDay(gfn_getAfterMonth(gfn_getCurrMonth(),3)+'01'));	// 현재월 + 3개월의 마지막 날
	ds_cond.fireevent = true;

	if(isExistVar("F_MENU", true) && F_MENU != '')
	{
		ed_pjt.Text   = s_pspid;
		ed_posid.Text = s_hno;
		ed_collect_bill = s_bill;
		fv_gbn = s_gbn;
	}
	else
	{
		ed_pjt.Text 	= ds_list.GetColumn(ds_list.row,"PSPID") ;   
		ed_posid.Text	= ds_list.GetColumn(ds_list.row,"POSID") ;  
		ed_collect_bill	= ds_list.GetColumn(ds_list.row,"COLLECT_BILL") ;  
	}


	fn_query();
	cb_desig_divis_OnClick(cb_desig_divis);

}
