/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() 
{

	tit_clearActionInfo();

	//tit_addSearchActionInfo("ps01:PS0101001A_S1");  //==> CsvSaerch로 변경
	//tit_addCsvSearchActionInfo("ps01:PS0101001A_S1"); 
	tit_addSearchActionInfo("ps02:PS0204008C_S1"); 
	//tit_addCsvSearchActionInfo("ps01:PS0101001A_S8");

	tit_addSearchActionInfo("ps02:PS0201001A_S72");
	tit_addSearchActionInfo("ps02:PS0204008C_S3");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_head ds_list_l=ds_list_l ds_day=ds_day"  //ds_head2=ds_list10"
        , ""
        , "fn_afterQuery"
        , ""
        , ""
        , true);


}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback 
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	if(ds_head.GetRowCount() > 0)
	{
		PUID = "";
		PUSER = "";

		// 생산관리담당자 추가
		tit_clearActionInfo();
		tit_callService(
		  "PS0101001A_ACT"
		, "getPrdUser"
		, "ds_cond=ds_cond"
		, ""
		, ""
		, ""
		, ""
		, true);

		ds_head.SetColumn(0, "P_PSNO", PUID);
		ds_head.SetColumn(0, "P_MAN", PUSER);
	}
	//gfn_showMsg("CI00002",ds_head.RowCount()+"");


}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
	tit_clearActionInfo();			
	//tit_addSelectExActionInfo("ps12:PS1204004B_S2", "ps12:PS1204004C_I1", "ps12:PS1204004C_U1");
	tit_addSelectExActionInfo("ps02:PS0204008C_S2", "ps02:PS0204008C_I1", "ps02:PS0204008C_U1");
	tit_callService(
		""
		, ""
		, "ds_head=ds_head"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);

}


/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {

	if ( errCode == 0 ) 
	{
		fn_close(); 
	}					

}



/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{

	if (!gfn_showConfirm("CI00015","착공예정일 변경요청")) 
	{
		return;
	}	

	tit_clearActionInfo();	
	//tit_addSingleActionInfo("bs04:BS0409002B_D2");
	tit_addSingleActionInfo("ps02:PS0204008C_D1");
	tit_callService(
		""
		, ""        
		, "ds_head=ds_head"
		, ""
		, ""
		, ""  //"fn_afterDelete"
		, ""
		, ""
		,true  // ""
		);	

	fn_close();

}

/********************************************************************************
* 기      능   : 닫기 
********************************************************************************/
function fn_close() 
{
	close();
}



