	ds_log.addRow();
	ds_log.SetColumn(0, "GBN", "M");	// 문자 : S , 메일 : M
	ds_log.SetColumn(0, "CLSS", "PS0204008C");
	ds_log.SetColumn(0, "GBN_DESC", "메일");
	ds_log.SetColumn(0, "CLSS_DESC", "착공예정일변경시 메일발송로그");
	ds_log.SetColumn(0, "SEND_USER", G_USER_ID);
	ds_log.SetColumn(0, "RECV_USER", ds_head.GetColumn(0, "I_PSNO"));
	ds_log.SetColumn(0, "EMAIL", ps_man_1);
	ds_log.SetColumn(0, "MESSAGE", mesgbody);

	ds_log.addRow();
	ds_log.SetColumn(1, "GBN", "M");	// 문자 : S , 메일 : M
	ds_log.SetColumn(1, "CLSS", "PS0204008C");
	ds_log.SetColumn(1, "GBN_DESC", "메일");
	ds_log.SetColumn(1, "CLSS_DESC", "착공예정일변경시 메일발송로그");
	ds_log.SetColumn(1, "SEND_USER", G_USER_ID);
	ds_log.SetColumn(1, "RECV_USER", ds_head.GetColumn(0, "I_PSNO2"));
	ds_log.SetColumn(1, "EMAIL", ps_man_2);
	ds_log.SetColumn(1, "MESSAGE", mesgbody);

	/*
	ds_log.addRow();
	ds_log.SetColumn(2, "GBN", "M");	// 문자 : S , 메일 : M
	ds_log.SetColumn(2, "CLSS", "PS0204008C");
	ds_log.SetColumn(2, "GBN_DESC", "메일");
