function fn_OnLoadCompleted(obj) 
{

	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	

	userGroup = gfn_getUserGroup();

	if(userGroup == '10' || userGroup == '20')
	{

		//alert(userGroup);

		//chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		cb_zzpmnum.Enable = true;
		//chk_so5.Enable = false;
		//chk_so6.Enable = false;
	}
	else if( userGroup == '30' )
	{
		//alert(userGroup);
		//chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		cb_zzpmnum.Enable = true;
		//chk_so5.Enable = true;
		//chk_so6.Enable = true;

		tit_clearActionInfo();		
		tit_addSearchActionInfo("ps02:PS0201001A_S32");
		tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"  // ds_pronr=ds_pronr"
		, ""
		, ""   //"fn_afterQueryC"
		, ""
		, ""
		, true);


	}
	else if( userGroup == '40' )
	{
		//alert(userGroup);
		//chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		cb_zzpmnum.Enable = true;
		//chk_so5.Enable = true;
		//chk_so6.Enable = true;
	}


	chk_so3.Value = '1';
	chk_so4.Value = '1';


	ed_inpday_f.value = substr(gfn_getCurrDate(),0,6)+"01";
	ed_inpday_t.value = gfn_getLeapLastDay(gfn_getCurrDate());



	ds_cond.SetColumn(0,"USER_GRUP",userGroup);
	if (userGroup == "10")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_USER_ID);
	}
	else if (userGroup == "30")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_AREA_CODE_E);
	}

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb01:SEOLCHI_VEND_S2");
	//tit_addSearchActionInfo("ps02:PS0205001A_S8");      //공정 코드

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzlifnr=ds_lifnr_cd"  // ds_pronr=ds_pronr"
		, ""
		, "fn_afterQueryC"
		, ""
		, ""
		, true);



	ed_inpday_f.SETFOCUS();
	gfn_showMsg("CI00007");

}

/********************************************************************************
* 기      능   :  협력사 조회 Callback  
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {
	cb_zzlifnr.Index = 0;

	if ( userGroup == "10" )
	{
		cb_zzlifnr.Enable = false;
	}
	else if ( userGroup == "30" || userGroup == "40")
	{
//alert(ds_zzlifnr.SaveXML());						
		ds_zzlifnr.filter("LOEVM!='X'" );		
		gfn_insertEmptyRow( ds_zzlifnr, G_CODE_ALL );
		cb_zzlifnr.value = "";
	}
}


/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) 
{

	//alert(userGroup);
	//alert(G_AREA_CODE_E);
	//alert(ds_zzactss.SaveXML());	

	if(userGroup == "40")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "30")
	{
		ds_zzactss.Filter("CODE="+quote(G_AREA_CODE_E));
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
	else if(userGroup == "10")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}

}


