function ds_list1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	obj.fireevent = false;




	switch( strColumnID ) 
	{
		case "ZSAFE":				
			ds_list1.SetColumn(nRow,"CHECK","1");
		case "ZINSP":
			ds_list1.SetColumn(nRow,"CHECK","1");
		case "ZWORK":
			ds_list1.SetColumn(nRow,"CHECK","1");
		case "ZWERP":
			ds_list1.SetColumn(nRow,"CHECK","1");
		case "BIGO":
			ds_list1.SetColumn(nRow,"CHECK","1");						
	}

	obj.fireevent = true;

    /*	
	obj.fireevent = false;	
	ds_list1.SetColumn(nRow,"CHECK", 1");	
	obj.fireevent = true;	
	*/
}



