********************************************************************************/
function btn_pjt_OnClick(obj)
{
	var arg = "pjt="+quote(ed_pjt.Text);

	gfn_openPopup("CS9601002A",true,arg);

	fn_query();
}

/********************************************************************************
* 기      능   : 리스트의 타이틀 클릭  
********************************************************************************/
function grd_list1_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		return;
	}
	else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}	
}




/********************************************************************************
* 기      능   : 프로젝트에서 엔터키 처리 
********************************************************************************/
function ed_pjt_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13)
	{
		fn_query();
	}
}


/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{

	// 그리드 저장 대상 로우
	if(ds_list1.FindRow("CHECK", 1)==-1)
		{
			gfn_showMsg('CW00046');
			return;
		}

		for(var i=0; i<ds_list1.GetRowCount(); i++)
		{
			if(ds_list1.GetColumn(i, "CHECK")==1)
			{
				/*
				// 필수 입력 항목	
				if(length(ds_list1.GetColumn(i, "PRONR"))<=0 )
				{
					var arr = [parseInt(i) + 1, "작업공정"];
					gfn_showMsg("CW00224", arr);
					return;
				} 
				*/
			}
		}

		ds_list1.Filter("CHECK==1");
		ds_list1_tmp.copyF(ds_list1);
		ds_list1.unFilter();	



		tit_clearActionInfo();
		tit_addMultiActionInfo("ps02:PS0205002A_U1");
		tit_callService(
          ""
        , ""
        , "ds_list1_tmp=ds_list1_tmp"
        , ""
        , ""
        , "fn_afterSave"
        , ""
        , ""
        , true);

		ds_list1_tmp.DeleteAll();
		//fn_query();		
	}

}


/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");				
	}
}

/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) 
{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");				
	}
}




/********************************************************************************
* 기      능   : 사무소콤보 변경시 설치PM 콤보값 생성
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_cond_cd.SetColumn(0,"ZZACTSS",strCode);
	cb_zzpmnum.value = "";

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S41");

	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond_cd"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"  // ds_pronr=ds_pronr"
		, ""
		, "fn_afterZzactssChange"
		, ""
		, ""
		, true);	

}

/********************************************************************************
* 기      능   : 조회 Callback  - 사무소 변경시
********************************************************************************/
function fn_afterZzactssChange(errCode, errMsg) {
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";
}
