function fn_save() 
{

	// 그리드 저장 대상 로우
	if(ds_list1.FindRow("CHECK", 1)==-1)
		{
			gfn_showMsg('CW00046');
			return;
		}

		for(var i=0; i<ds_list1.GetRowCount(); i++)
		{
			if(ds_list1.GetColumn(i, "CHECK")==1)
			{

				//막을것
				/*
				//안전검사 1단계/2단계 체크
				if( ds_list1.GetColumn(i, "SAFE1") != 1 && ds_list1.GetColumn(i, "SAFE2") == 1 )
				{
					var arr = [parseInt(i) + 1, "안전1단계 완료가 되지 않을시 안전2단계 완료불가 하므로"];
					gfn_showMsg("CW00055", arr);					
					return;					
				}
				*/

			}
		}

		ds_list1.Filter("CHECK==1");
		ds_list1_tmp.copyF(ds_list1);
		ds_list1.unFilter();	



		tit_clearActionInfo();
		tit_addMultiActionInfo("ps02:PS0206002A_U1");
		tit_callService(
          ""
        , ""
        , "ds_list1_tmp=ds_list1_tmp"
        , ""
        , ""
        , "fn_afterSave"
        , ""
        , ""
        , true);

		ds_list1_tmp.DeleteAll();
		//fn_query();		
	}

}


