function fn_query() 
{

//alert(ds_cond.SaveXML());	


    if ( !gfn_checkFromToObj(ed_inpday_f, ed_inpday_t, true, st_date.value, 0, true) ) 
    {
		return;
	}


	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps02:PS0206004A_S1");  	
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list1=ds_list1"  	
        , ""
        , "fn_afterQuery"  
        , ""
        , ""          
        , true);


}

