/********************************************************************************
* 기      능   : 리스트의 타이틀 클릭  
********************************************************************************/
function grd_list1_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{

     if ( nCell == 0 ) {
		// 현재 처리할 format 기억
		gfn_selectGridCol("grd_list");
	} else {
		gfn_sortGrid(obj,nCell);
	}
}



/********************************************************************************
* 기      능   : 설치협력사 변경시 이름 콤보값 생성
********************************************************************************/

function cb_zzlifnr_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    ds_cond_cd.SetColumn(0,"ZZLIFNR",ds_cond.GetColumn(0,"ZZLIFNR"));

  //  alert(ds_cond_cd.GetColumn(0,"ZZLIFNR"));
	//ds_cond_cd.SetColumn(0,"ZZLIFNR",strCode);
	cb_zzsawonnum.value = "";

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S61");

	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond_cd"
		, "ds_zzsawonnum_cd=ds_zzsawonnum_cd"  // ds_pronr=ds_pronr"
		, ""
		, "fn_afterZzlifnrChange"
		, ""
		, ""
		, true);	
}

/********************************************************************************
* 기      능   : 조회 Callback  - 설치협력사 변경시
********************************************************************************/
function fn_afterZzlifnrChange(errCode, errMsg) {
	gfn_insertEmptyRow( ds_zzsawonnum_cd, G_CODE_ALL );
	cb_zzsawonnum.value = "";
}


/********************************************************************************
* 기      능   : 사무소 별 협력업체 필터 
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(userGroup == "40" || userGroup == "30"){
    ds_cond.SetColumn(0,"LIFNR",''); }

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_zzlifnr.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_zzlifnr.Filter("ZZACTSS ==" +quote(arr));
    }
}
