function fn_OnLoadCompleted(obj) 
{

	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	

	userGroup = gfn_getUserGroup();

	if(userGroup == '10' || userGroup == '20')
	{

		chk_so3.Enable = false;
		chk_so4.Enable = false;
		cb_zzpmnum.Enable = true;

	}
	else if( userGroup == '30' )
	{

		chk_so3.Enable = true;
		chk_so4.Enable = true;
		cb_zzpmnum.Enable = true;


		tit_clearActionInfo();		
		tit_addSearchActionInfo("ps02:PS0201001A_S32");
		tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"  // ds_pronr=ds_pronr"
		, ""
		, ""   //"fn_afterQueryC"
		, ""
		, ""
		, true);


	}
	else if( userGroup == '40' )
	{

		chk_so3.Enable = true;
		chk_so4.Enable = true;
		cb_zzpmnum.Enable = true;

	}


	chk_so3.Value = '1';
	chk_so4.Value = '1';


	ed_inpday_f.value = gfn_getCurrDate();
	 //substr(gfn_getCurrDate(),0,6)+"01";
	ed_inpday_t.value = gfn_getCurrDate();



	ds_cond.SetColumn(0,"USER_GRUP",userGroup);
	if (userGroup == "10")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_USER_ID);
	}
	else if (userGroup == "30")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_AREA_CODE_E);
	}

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	ds_cond.SetColumn(0, "CLSSFY", 'A');

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S34");
	//tit_addSearchActionInfo("ps02:PS0205001A_S8");      //공정 코드

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzlifnr=ds_zzlifnr_cd"  // ds_pronr=ds_pronr"
		, ""
		, "fn_afterQueryC"
		, ""
		, ""
		, true);



	ed_inpday_f.SETFOCUS();
	gfn_showMsg("CI00007");

}

