function fn_OnLoadCompleted(obj) {
	gfn_initForm();
	ufn_initForm();
//alert(F_CHK);

//alert(G_MANDT);
//alert(substr(F_POSID,0,6));
	//수금전 진행여부 조회 rfc function호출
	tit_clearActionInfo();
	tit_callService(
		"COMM_ACT"
		, "getSdReturnDate"
		, ""
		, ""
		, "I_PSPID="+quote(substr(F_POSID,0,6)) + " I_GUBUN='D' I_DATE="+quote(gfn_getCurrDate())
		, "fn_afterDate"
		,true,true,true);

	// 개발에서 테스트를 위하여
	if ( G_MANDT == '910' || G_MANDT == '310' )
	{
		E_FLAG = 'P';
	}	

	trace("E_FLAG == " + E_FLAG);
	trace("F_CHK == " + F_CHK);


//alert(G_MANDT);
//alert(gfn_getUserGroup());

//alert(E_FLAG);
	//수금전 진행상태가 'P'(진행)이 아닐경우 공정진행 입력 금지 20100820 sjy
	if(E_FLAG != 'P' || F_CHK == 'N')
	{
		//div_btn.btn_save.enable = false;
	}
//ALERT(F_TYPE);	

    F_CHK2 = 'N'; //기본 셋팅 
	fn_query();
}
