/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_pop);
	ds_pop.row = -1;
	ds_pop.ClearData();

//ALERT("F_POSID : " + F_POSID);
//ALERT("F_GUBUN : " + F_GUBUN);
//ALERT("F_TYPE : " + F_TYPE);
	var args = "POSID="+quote(F_POSID);
		args += " PSPID="+quote(F_PSPID);
		args += " GUBUN="+quote(F_GUBUN);
		args += " TYPE="+quote(F_TYPE);
		args += " SAFE="+quote(F_SAFE);
		args += " VER="+quote(F_VER);//2019.03.13 강민석K 추가

	//trace("VER : " + quote(F_VER));

	var q1="";	
	var q2="";	
	var q3="";	

	var cRow=0;

			q1 += ", '0' COL"
				+ ", 'N' B_COL"

			    //추가(안전검사관련)//
			    + ",  '" + F_SAFE + "' SAFE"
  		        + "\n ,VALUE(MIN((CASE WHEN RIGHT(R03.POSID_1, 3)='"+right(F_POSID,3)+"' THEN "
    			+ "\n (CASE  WHEN (T13.MDATE)  <> '' THEN '1' ELSE '0' END)  "
				+ "\n END)), '0') JCOL";

		   q1 += "\n ,CASE WHEN (SELECT X.LIFNR FROM SAPHEE.ZMASTER02 AS X WHERE X.MANDT = A.MANDT "
		       + "\n  AND X.POSID= '"+ SUBSTR(F_POSID,0,6) +"' AND X.POSID_1 = '"+ F_POSID+"') = '0000008001' THEN '1'"
	           + "\n  WHEN (SELECT X.ZZILSIC FROM SAPHEE.ZPST0009 AS X WHERE X.MANDT = A.MANDT "
	           + "\n  AND X.POSID= '"+ SUBSTR(F_POSID,0,6) +"' AND X.POSID_1 = '"+ F_POSID+"') = 'X' THEN '1' ELSE '0' END AS DIR";		



			q2 += "\n , VALUE(MIN((CASE WHEN RIGHT(R02.POSID_1, 3)='"+right(F_POSID,3)+"' THEN "
				+ "\n (CASE WHEN (T12.CRDAT)='00000000' THEN '0'" 
				+ "\n WHEN  (T12.CRDAT)=HEX(CURRENT DATE) THEN '1' ELSE CHAR(T12.CRDAT) END) "
				+ "\n END)), '0') COL";

			q2 += "\n , 'N' B_COL";

			//추가(안전검사관련)//						
			q2 += "\n,  '" + F_SAFE + "' SAFE" 
			    + "\n ,VALUE(MIN((CASE WHEN RIGHT(R03.POSID_1, 3)='"+right(F_POSID,3)+"' THEN "
    			+ "\n (CASE  WHEN (T13.MDATE)  <> '' THEN '1' ELSE '0' END)  "
				+ "\n END)), '0') JCOL";


		    q2 += "\n ,CASE WHEN (SELECT X.LIFNR FROM SAPHEE.ZMASTER02 AS X WHERE X.MANDT = T11.MANDT "
		       + "\n  AND X.POSID= '"+ SUBSTR(F_POSID,0,6) +"' AND X.POSID_1 = '"+ F_POSID +"') = '0000008001' THEN '1'"
		       + "\n  WHEN (SELECT X.ZZILSIC FROM SAPHEE.ZPST0009 AS X WHERE X.MANDT = T11.MANDT "
	           + "\n  AND X.POSID= '"+ SUBSTR(F_POSID,0,6) +"' AND X.POSID_1 = '"+ F_POSID+"') = 'X' THEN '1' ELSE '0' END AS DIR";	

            q3 += "\n , T.COL"
               + "\n , T.B_COL"
               + "\n , T.SAFE"
               + "\n , T.JCOL"
               + "\n , T.DIR";

			cRow++;



		args += " S1_QUERY="+quote(q1);
		args += " S2_QUERY="+quote(q2);
		args += " S3_QUERY="+quote(q3);

		//alert(q1);
      // alert(q2);
		//alert(F_ZSPEC);
	//	alert(F_ZZILSIC);


	  //  trace(ds_pop1.SaveXML());	
//trace(args);
//alert("xxxx");

	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps02:PS0207003B_S1");
	tit_addSearchActionInfo("ps02:PS0207003B_S2");
	tit_addSearchActionInfo("ps02:PS0207003B_S3");

	// Trace(q2);

	tit_callService(
        ""
        , ""
        , ""
        , "ds_pop1=ds_pop1 ds_pop2=ds_pop2 ds_pop3=ds_pop3"
        , args
        , "fn_afterQuery"
        , true);
}
