									ds_pop.SetColumn(r, "COL"+substr(ds_pop.GetColID(c), 4), "");	//안전마감X 
								}
							}
						}

					}
					*/					
					else if(ds_pop.GetColumn(r, c) = 'Z' )
					{
					  /*
					  if(ds_pop.GetColumn(r, "PRONR") >= '01' && ds_pop.GetColumn(r, "PRONR") <= '37')
