function fn_afterQuery2(errCode, errMsg) {
	// 메시지 처리 
	gfn_showSearchMsg(tab00.tab2.grd_list3);
}


/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( ds_error.rowcount() > 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}
}
