var userGroup = "";
var args = "";
var curr_date = "";
//var userGroup = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{

	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(false);	

	// 10 : 보수협력사
	// 20 : 보수PM
	// 30 : 지방사무소 
	// 40 : 고객지원부	
	userGroup = gfn_getUserGroup();	

	// 좌측 라디오버튼의 default
	/*
	// 04.13 라디오버튼 반려 버튼 제거
	if(userGroup == '10' || userGroup == '20')  // 1. 협력사가 로그인한 경우  [반려만]
	{		
		ds_cond.SetColumn(0,"APRCD","R");
		btn_cancel.Enable = false;
		btn_apr.Enable = false;
	}
	else */
	if( G_USER_GROUP == 'Z30' )  // 2. 설치본부가 로그인한 경우 [승인대기만]
	{
		ds_cond.SetColumn(0,"APRCD","N");	
		btn_cancel.Enable = true;
		btn_apr.Enable = true;
	}
	else // 3. 설치본부 외 로그인한 경우 [승인만]
	{
		ds_cond.SetColumn(0,"APRCD","Y");
		btn_cancel.Enable = true;
		btn_apr.Enable = true;
	}

	ed_inpday_f.value = gfn_getAfterDate(gfn_getCurrDate(),-30);
	ed_inpday_t.value = gfn_getCurrDate();

	ds_cond.SetColumn(0,"USER_GRUP",userGroup);
	if (userGroup == "10")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_USER_ID);
	}
	else if (userGroup == "30")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_AREA_CODE_E);
	}

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);


	// 설치협력사
	if ( userGroup == "10" )
	{
		ds_cond.fireevent = false;
		ds_cond.SetColumn(0, "ZZLIFNR", G_VEND_CODE_E);
		ds_cond.SetColumn(0, "ZZLIFNR_NM", G_VEND_NAME);
		ds_cond.fireevent = true;

		edt_lifnr.Enable = false;
		btn_lifnr.Enable = false;

	}
	else if ( userGroup == "30" || userGroup == "40")
	{	
		edt_lifnr.Enable = true;
		btn_lifnr.Enable = true;

	}
}


/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) 
{

	if(userGroup == "40")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "30")
	{
		//ds_zzactss.Filter("CODE="+quote(G_AREA_CODE_E));
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "10")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}

}


