/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() 
{
	ApplyData();

	// 설치 사무소 또는 협력사 중 하나는 필수 입력으로 제한 - 2020/01/22(고범석 CJN)
	var sZzlifnr = ds_cond.GetColumn(0, "ZZLIFNR");
	var sZzactss = ds_cond.GetColumn(0, "ZZACTSS");
	var sPspid   = ds_cond.GetColumn(0, "PSPID");

	if(Length(sZzlifnr) == 0 && Length(sZzactss) == 0 && Length(sPspid) == 0)
	{
		gfn_openMsgWin("프로젝트, 사무소 또는 설치협력사 중 하나는\n필수 선택입니다.");
		return;
	}

    if ( !gfn_checkFromToObj(ed_inpday_f, ed_inpday_t, true, st_date.value, 0, true) ) 
    {
		return;
	}    

	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps02:PS0207004A_S1");  	
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list1=ds_list1"  
        , ""
        , "fn_afterQuery"  
        , ""
        , ""
        , true);

}


/********************************************************************************
* 기      능   : 닫기 
********************************************************************************/
function fn_close() 
{
	close();
}

