function btn_lifnr_OnClick(obj)
{
	var arg = 'F_ZZACTSS='+quote(ds_cond.getColumn(0, "ZZACTSS"));
	var arr = gfn_openPopup("PS0208001D", true, arg);

	if(Length(arr) > 0)
	{
		//ds_list.fireevent = false;
		ds_cond.SetColumn(0, "ZZLIFNR", arr[0]);
		ds_cond.SetColumn(0, "ZZLIFNR_NM", arr[1]);

		edt_lifnr_OnChanged();
		//ds_list.fireevent = true;
	}
}


/********************************************************************************
* 기      능   : 설치협력사 조회조건 변경시
********************************************************************************/
function ds_cond_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'ZZLIFNR_NM')
	{
		if(length(Trim(varNewValue)) == 0)
		{
			ds_cond.SetColumn(0, "ZZLIFNR", "");
			ds_cond.SetColumn(0, "ZZLIFNR_NM", "");
			return;
		}

		// 존재하는 사업자번호 체크 (미존재시 팝업)
		nFindRow = ufn_chkLifnr(varNewValue);

		if(nFindRow < 0)	// 사업자 번호 미존재시 팝업을 띄워준다.
		{
			var arg = "";
			var arr = gfn_openPopup("PS0208001D", true, arg);

			if(Length(arr) > 0)
			{
				ds_cond.SetColumn(0, "ZZLIFNR", arr[0]);
				ds_cond.SetColumn(0, "ZZLIFNR_NM", arr[1]);
			}

		}
		else
		{
			if(nFindRow == 1)
			{
				ds_cond.SetColumn(0, "ZZLIFNR", ds_lifnr_grid.GetColumn(0, "CODE"));
				ds_cond.SetColumn(0, "ZZLIFNR_NM", ds_lifnr_grid.GetColumn(0, "CODE_NAME"));
			}
			else
			{
				var arg = "F_LIFNR_N="+quote(varNewValue);
				var arr = gfn_openPopup("PS0208001D", true, arg);

				if(Length(arr) > 0)
				{
					ds_cond.SetColumn(0, "ZZLIFNR", arr[0]);
					ds_cond.SetColumn(0, "ZZLIFNR_NM", arr[1]);
				}
			}

		}
	}
}

function ufn_chkLifnr(lifnr)
{
	var rValue = 0;

	ds_condLifnr.ClearData();
	ds_condLifnr.addRow();
	ds_condLifnr.SetColumn(0, "LIFNR", lifnr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0208001D_S2");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_condLifnr"
		, "ds_lifnr_grid=ds_lifnr_grid"
		, ""
		, "" // fn_afterQueryC
		, ""
		, ""
		, true);

	if(ds_lifnr_grid.GetRowCount() == 0)
	{
		rValue = -1;
	}
	else
	{
		rValue = ds_lifnr_grid.GetRowCount();
	}

	return rValue;
}



function ds_list1_OnRowPosChanged(obj,nOldRow,nRow)
{

	var url = GF_FILE_SERVER_URL;

	if (IndexOf(url, "localhost")) {
		//url = Replace(url, "localhost:8088", "203.242.37.24:8080");
	}				

	var fnameApp = ds_list1.GetColumn(ds_list1.currow , "FNAME_APP");

	url = Replace(url, "/HELCO_WEB/", "/") + 'upload/PS/' + fnameApp;

	Image0.ImageID = url;	

	if( nRow != null ) {
		var aprcd = ds_list1.GetColumn(ds_list1.currow , "APRCD");

		if( aprcd == "Y" ) { //승인 
			btn_cancel.Enable = false;
			btn_apr.Enable = false;
		} else if( aprcd == "R" ) { // 반려
			btn_cancel.Enable = false;
			btn_apr.Enable = true;
		} else { 
			btn_apr.Enable = true;
			btn_cancel.Enable = true;
		}
	}

}

/********************************************************************************
* 기      능   : 승인버튼 클릭
********************************************************************************/
function btn_apr_OnClick(obj)
{	
	if( ds_list1.currow < 0 ) {
		alert("리스트에서 안전조치 정보를 선택해 주세요.");
		return;
	}

	if(confirm("승인하시겠습니까?")) {

		ds_list1_m.DeleteAll();
		ds_list1_m.AddRow();		

		ds_list1_m.CopyRow(ds_list1_m.currow, ds_list1,ds_list1.currow);		
		ds_list1_m.SetColumn(ds_list1_m.currow, "APRCD" , "Y" );		

		tit_clearActionInfo();	
		tit_addSingleActionInfo("ps02:PS0207004A_I1");

		tit_callService(
			  ""
			, ""
			, "ds_list1_m=ds_list1_m"
			, ""  
			, ""
			, "fn_afterSave"
			, ""
			, ""
			, true);

	}
}

/********************************************************************************
* 기      능   : 승인콜백
********************************************************************************/
function fn_afterSave(errCode, errMsg)
{
	if ( errCode != null && errCode != 0  && errCode != '') 
	{
		gfn_showAlert("CE00001");
	}
	else
	{
		gfn_openMsgWin("승인처리 되었습니다.");
		fn_query();
	}	

}        

/********************************************************************************
* 기      능   : 취소버튼 클릭
********************************************************************************/
function btn_cancel_OnClick(obj)
{
	if( ds_list1.currow < 0 ) {
		alert("리스트에서 안전조치 정보를 선택해 주세요.");
		return;
	}

	if(confirm("반려 하시겠습니까?")) {

		ds_list1_m.DeleteAll();
		ds_list1_m.AddRow();		

		ds_list1_m.CopyRow(ds_list1_m.currow, ds_list1,ds_list1.currow);				

		// 승인취소(반려) R
		ds_list1_m.SetColumn(ds_list1_m.currow, "APRCD" , "R" );

		tit_clearActionInfo();	
		tit_addSingleActionInfo("ps02:PS0207004A_I1");

		tit_callService(
			  ""
			, ""
			, "ds_list1_m=ds_list1_m"
			, ""  
			, ""
			, "fn_afterSave2"
			, ""
			, ""
			, true);
	}
}

