function fn_afterSave2(errCode, errMsg)
{
	if ( errCode != null && errCode != 0  && errCode != '') 
	{
		gfn_showAlert("CE00001");
	}
	else
	{
		gfn_openMsgWin("반려 처리 되었습니다.");

		// SMS 
		fn_sendSms();	

		//fn_query();
	}

	//ds_head.fireevent = true;	
}   

/********************************************************************************
* 기      능   : SMS 전송
********************************************************************************/
function fn_sendSms(arr) {
	var tel;
	var txt;
	var msg;

	if(arr == "C") {
		gfn_showAlert("메세지 전송이 불가능한 상태입니다.");
		return;
	}

	// 반려사유 입력 팝업
	var arg = "";
	var reason = "";

	var rtn = gfn_openPopUpUrl("PS02::PS0207004B.xml", true, arg);

	if ( rtn !=null && rtn != "" && rtn != "0" ) {			
		reason = rtn;
	} 

	trace( "reason => " + reason);

	ds_sms.ClearData();

	msg = "[안전조치승인] "
		+ ds_list1.GetColumn(ds_list1.currow, "POSID_1") + " "
		 + ds_list1.GetColumn(ds_list1.currow, "ZSITE_NM")+ "의 "
		 + ds_list1.GetColumn(ds_list1.currow, "GBN_NM")+ " "
		 + " 안전조치 승인이 반려됐으니 보완조치 후 사진 재 등록 바랍니다. "; 

    if(reason  != "" ) { 
		msg += " 반려사유 : " + reason;
    }

    trace(msg);

	tel = ds_list1.GetColumn(ds_list1.currow, "CMBPN");

	ds_sms.AddRow();
	ds_sms.SetColumn(0, "FLAG", "U");
	ds_sms.SetColumn(0, "TRAN_SUBJECT", "[안전 조치 승인]");
	ds_sms.SetColumn(0, "TRAN_PHONE", tel);
	//ds_sms.SetColumn(0, "TRAN_PHONE", "010-2300-1053");
	ds_sms.SetColumn(0, "TRAN_CALLBACK", '02-3670-1213');
	ds_sms.SetColumn(0, "TRAN_STATUS", "0");
	ds_sms.SetColumn(0, "TRAN_DATE", "");
	ds_sms.SetColumn(0, "TRAN_MSG", msg);	
	ds_sms.SetColumn(0, "REQDATE", "CURRNET");	

	tit_clearActionInfo();
	tit_addSaveActionInfo(
        ""
        , "ps02:PS0207004A_I2"
        , ""
        , "FLAG");
	tit_callService(
        "PDA_ACT"
        , ""
        , "ds_sms=ds_sms:A"
        , ""
        , ""
        , "fn_afterSms");
}

function fn_afterSms(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showAlert("메세지 전송이 완료되었습니다."); 

		fn_query();
	}
}

