#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var userGroup = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	

	userGroup = gfn_getUserGroup();

	if(userGroup == '10')
	{
		chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;
	}
	else
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;
	}

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-5,10);
	cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_montht.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;

	ds_cond.SetColumn(0,"USER_GRUP",userGroup);
	if (userGroup == "10")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_USER_ID);
	}
	else if (userGroup == "30")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_AREA_CODE_E);
	}

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	tit_clearActionInfo();
	//tit_addSearchActionInfo("wb01:SEOLCHI_VEND_S2");

    tit_addSearchActionInfo("ps02:PS0201001A_S34");

	tit_callService(
	  ""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_lifnr=ds_zzlifnr_cd"
		, ""
		, "fn_afterQueryC"
		, true,true);

	cb_yearf.SetFocus();
}

/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		ds_yeart_cd.AddRow();
		ds_yeart_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yeart_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_yearf.index = eNum;
	cb_yeart.index = eNum;
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	if(gfn_isNull(cb_yearf, true, st_yymm.value)) return;
	if(gfn_isNull(cb_monthf, true, st_yymm.value)) return;
	if(gfn_isNull(cb_yeart, true, st_yymm.value)) return;
	if(gfn_isNull(cb_montht, true, st_yymm.value)) return;
	if(parseInt(ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF")) > 
	   parseInt(ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT")))
	{
		gfn_showMsg("CW00009");
		cb_yeart.SetFocus();
		return;
	}   

	//설치협력사필드 필수체크(설치협력사 로그인시)
	if( userGroup == '10' && gfn_isNull(cb_lifnr, true, st_lifnr.value)) return;

	var fYear  = parseInt(ds_cond.GetColumn(0,"YEARF"));
	var tYear  = parseInt(ds_cond.GetColumn(0,"YEART"));
	var fMonth = parseInt(ds_cond.GetColumn(0,"MONTHF"));
	var tMonth = parseInt(ds_cond.GetColumn(0,"MONTHT"));

	if(length(trim(ed_pjt.Text)) == 6) ds_cond.SetColumn(0,"GBN","A");
	else ds_cond.SetColumn(0,"GBN","B");

	//조회기간: 0=같은달,1=2개월,2=3개월..
	if(ds_cond.GetColumn(0,"GBN") == "B" && ((tYear-fYear)*12+tMonth-fMonth) > 11)
	{
		gfn_showMsg("CW00078"," 12 ");
		cb_montht.SetFocus();
		return;
	}

	ds_cond.SetColumn(0,"YYYYMMF",ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF"));
	ds_cond.SetColumn(0,"YYYYMMT",ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT"));

	if(chk_10.value == 0 && 
	   chk_11.value == 0 && 
	   chk_20.value == 0 )
		ds_cond.SetColumn(0,"CHK_GBN","A");
	else
		ds_cond.SetColumn(0,"CHK_GBN","B");

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps04:PS0401001A_S2");  변경전
	tit_addSearchActionInfo("ps04:PS0401001A_S3");  //==> CsvSaerch로 변경
	//tit_addCsvSearchActionInfo("ps04:PS0401001A_S3"); 	

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , ""
		, ""
        , true);

    //=============================수정부분=============================
    var down_rate = 0.00;
    for( var i=0; i<ds_list.rowcount; i++ )
	{
		down_rate = 0.00;	
		down_rate = ds_list.GetColumn(i,"RAIL_F") + ds_list.GetColumn(i,"RAIL2_F") + ds_list.GetColumn(i,"JAMB_F") + ds_list.GetColumn(i,"SILL_F") + 
		            ds_list.GetColumn(i,"CWT") + ds_list.GetColumn(i,"BUFFER") + ds_list.GetColumn(i,"MBEAM") ;

		ds_list.SetColumn(i,"DOWN_RATE", down_rate);				        
    }
   //====================================================================

}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}
/********************************************************************************
* 기      능   :  협력사 조회 Callback  
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {
	//cb_lifnr.Index = 0;

	if ( userGroup == "10" )
	{
	    ds_lifnr.Filter("CODE=="+quote(G_VEND_CODE_E));
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = false;
	}
	else if ( userGroup == "30" || userGroup == "40")
	{
		ds_lifnr_cd.Filter("SO5==''");
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
		cb_lifnr.value = "";
	}
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	if(userGroup == "40")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "30")
	{
		ds_zzactss.Filter("CODE="+quote(G_AREA_CODE_E));
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
	else if(userGroup == "10")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
