* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(grd_list, true );
}
/********************************************************************************
* 기      능   : Grid의 '기타'항목을 Click시, 부가사항 상세정보 POP-UP화면으로 연동
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	//if(nCell == 16 && userGroup == '40')
	if(nCell == 20) //정태성씨 요구 모든권한 상세정보 볼수있도록 수정
	{
		gfn_openPopup("PS0401001B",true);
	}

	if(nCell == 9) //정태성씨 요구로 개발
	{
		gfn_openPopup("PS0401001C",true);
	}	


}
/********************************************************************************
* 기      능   : 통합프로젝트 검색 팝업
