//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm();	

	ds_cond_p.SetColumn(0,"PSPID",ds_list.GetColumn(ds_list.row,"PSPID"));
	ds_cond_p.SetColumn(0,"POSID",ds_list.GetColumn(ds_list.row,"POSID"));
	ds_cond_p.SetColumn(0,"CHGNO",ds_list.GetColumn(ds_list.row,"CHGNO"));

	ed_lifnr.value = ds_list.GetColumn(ds_list.row,"LIFNR_NM");
	ed_namet.value = ds_list.GetColumn(ds_list.row,"NAMET");
	med_yymm.value = ds_list.GetColumn(ds_list.row,"YYYYMM");
	ed_kinds.value = ds_list.GetColumn(ds_list.row,"KINDS");
	ed_spect.value = ds_list.GetColumn(ds_list.row,"SPECT");

	//====================================================================
	ed_rail_f.value   = ds_list.GetColumn(ds_list.row,"RAIL_F");
	//ed_rail_f_m.value = ds_list.GetColumn(ds_list.row,"RAIL_F") / 100 * ds_list.GetColumn(ds_list.row,"SUB_T");

	ed_rail2_f.value   = ds_list.GetColumn(ds_list.row,"RAIL2_F");
	//ed_rail2_f_m.value = ds_list.GetColumn(ds_list.row,"RAIL2_F") / 100 * ds_list.GetColumn(ds_list.row,"SUB_T");

	ed_jamb_f.value    = ds_list.GetColumn(ds_list.row,"JAMB_F");
	//ed_jamb_f_m.value  = ds_list.GetColumn(ds_list.row,"JAMB_F") / 100  * ds_list.GetColumn(ds_list.row,"SUB_T");

	ed_sill_f.value    = ds_list.GetColumn(ds_list.row,"SILL_F");
	//ed_sill_f_m.value  = ds_list.GetColumn(ds_list.row,"SILL_F") / 100  * ds_list.GetColumn(ds_list.row,"SUB_T");

	ed_cwt.value       = ds_list.GetColumn(ds_list.row,"CWT");
	//ed_cwt_m.value     = ds_list.GetColumn(ds_list.row,"CWT") / 100  * ds_list.GetColumn(ds_list.row,"SUB_T");

	ed_buffer.value    = ds_list.GetColumn(ds_list.row,"BUFFER");
	//ed_buffer_m.value  = ds_list.GetColumn(ds_list.row,"BUFFER") / 100  * ds_list.GetColumn(ds_list.row,"SUB_T");

	ed_mbeam.value     = ds_list.GetColumn(ds_list.row,"MBEAM");
	//ed_mbeam_m.value   = ds_list.GetColumn(ds_list.row,"MBEAM") / 100  * ds_list.GetColumn(ds_list.row,"SUB_T");


	//=====================================================================


	fn_query();
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps04:PS0401001B_S1");  //==> CsvSaerch로 변경
	tit_addCsvSearchActionInfo("ps04:PS0401001B_S1"); 

	tit_callService(
		""
		, ""
		, "ds_cond_p=ds_cond_p"
		, "ds_list_h=ds_list_h"
		, ""
		, ""  //"fn_afterQuery"
		, true);

}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	//gfn_showMsg("CI00002",ds_list_p.RowCount()+"");
}

