var userGroup = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);	

	userGroup = gfn_getUserGroup();


    var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
	if(userGroup == '10')
	{
		chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;
	}
	else
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;
	}

	ds_cond.SetColumn(0,"USER_GRUP",userGroup);
	if (userGroup == "10")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_USER_ID);
	}
	else if (userGroup == "30")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_AREA_CODE_E);
	}

	ds_cond.SetColumn(0, "CLSSFY", 'A');

	tit_clearActionInfo();
	//tit_addSearchActionInfo("wb01:SEOLCHI_VEND_S2");
	tit_addSearchActionInfo("ps02:PS0201001A_S34");

	tit_callService(
	  ""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_lifnr=ds_zzlifnr_cd"
		, ""
		, "fn_afterQueryC"
		, true,true);

	ed_pjt.SetFocus();
}
/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	//설치협력사필드 필수체크(설치협력사 로그인시)
	if( userGroup == '10' && gfn_isNull(cb_lifnr, true, st_lifnr.value)) return;

	if(length(trim(ed_pjt.Text)) != 6)
	{
		var arr = ["프로젝트","6"];
		gfn_showMsg("CW00174",arr);
		return;
	}

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps04:PS0401003A_S1");  //==> CsvSaerch로 변경
	tit_addCsvSearchActionInfo("ps04:PS0401003A_S1"); 

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	//누적공정율,누적기성율 계산
	if(ds_list.rowcount > 0) fn_calcNujuk();
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_calcNujuk() {
	var posid = "";
	var pro_r = 0;
	var wrbtr = 0;
	for(var i=0; i<ds_list.rowcount; i++)
	{
		if(posid == ds_list.GetColumn(i,"POSID"))
		{
			pro_r = ParseFloat(pro_r) + ParseFloat(ds_list.GetColumn(i,"PRO_R"));
			wrbtr = ParseFloat(wrbtr) + ParseFloat(ds_list.GetColumn(i,"WRBTR"));
		}
		else
		{
			pro_r = ParseFloat(ds_list.GetColumn(i,"PRO_R"));
			wrbtr = ParseFloat(ds_list.GetColumn(i,"WRBTR"));
		}
		ds_list.SetColumn(i,"PRO_R2",pro_r);
		ds_list.SetColumn(i,"WRBTR2",wrbtr);
		posid = ds_list.GetColumn(i,"POSID");
	}
	//for loop돌면서 누계값 세팅하면 화면 깜빡임 ==> dataset에 copy해서 사용
	ds_list2.Copy(ds_list);
}
/********************************************************************************
* 기      능   :  협력사 조회 Callback  
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {
	cb_lifnr.Index = 0;

	if ( userGroup == "10" )
	{
		ds_lifnr.Filter("CODE=="+quote(G_VEND_CODE_E));
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = false;
	}
	else if ( userGroup == "30" || userGroup == "40")
	{
		ds_lifnr.Filter("SO5==''");
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
		cb_lifnr.value = "";
		cb_zzactss.value = "";
	}
}
