    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

    grd_list.Format = "Default";
    rd_gbn.Index= 0;
    ds_cond_cd.SetColumn(0,"USERID",G_USER_ID);
	//zpst1116 SRM 우회 ID CHK
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps04:PS0401005A_S3");
	tit_callService(
	  ""
    , ""
	, "ds_cond_cd=ds_cond_cd"
	, "ds_autal=ds_autal"
	, ""
	, ""//"ufn_afterdaesuCount"
	, ""
    , ""
    , true);	

	//cal_date.SetFocus();
}

