/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	if(Length(Trim(ed_pjt.value)) < 6)
	{
		gfn_showMsg("CW10025", "프로젝트 번호는 필수입력입니다.(6자리)");
		return;
	}
    //if(gfn_isNull(ed_pjt, true, st_pjt.value)) return;

	tit_clearActionInfo();
	//tit_addCsvSearchActionInfo("ps04:PS0401006A_S1"); // EL만 있는 현장인지, ES만 있는 현장인지 확인. 1:EL만 2:ES만 3:혼용
	//tit_addSearchActionInfo("ps04:PS0401005A_S1");  //==> CsvSaerch로 변경
	if(grd_list.Format = "Default"){
	  tit_addCsvSearchActionInfo("ps04:PS0401006A_S1");}
	else if(grd_list.Format = "Default1"){
 	  tit_addCsvSearchActionInfo("ps04:PS0401006A_S2");}
	else if(grd_list.Format = "Default2"){ 
	  tit_addCsvSearchActionInfo("ps04:PS0401005A_S4");}
	else
	{
		tit_addCsvSearchActionInfo("ps04:PS0401005A_S5");
	}

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);

     // 현장관리비 SUM만 재조회 (2018.12.12 계원찬 SW 요청사항)
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps04:PS0401006A_S3");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_sum=ds_sum"
        , ""
        , "fn_afterQuerySum"
        , true);


}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	// 마무리공사비 예산 수정
    var bal = 0;
    for( var i=0; i<ds_list.rowcount; i++ )
	{ 
		bal = 0;
		if(ds_list.GetColumn(i, 'ZZGUBUN') != '11' && ds_list.GetColumn(i, 'ZZGUBUN') != '12')
		{
			if(ToFloat(ds_list.GetColumn(i,"EQME93")) > 0 )
			{		
			  if (ToFloat(ds_list.GetColumn(i,"ACAPA")) >= 1350 ) 
			   { //alert("a");
				 bal = ( 250000 * 1 ) + ( ( ToFloat(ds_list.GetColumn(i,"AFQ")) - 15 ) * 8000 ) ;
			   }
			   else if (ToFloat(ds_list.GetColumn(i,"ACAPA")) > 750 and ToFloat(ds_list.GetColumn(i,"ACAPA")) < 1350 ) 
			   { //alert("b");
				 bal =  ( ( 250000 * 85 ) / 100 ) + ( ( ToFloat(ds_list.GetColumn(i,"AFQ"))  - 15 ) * 8000 ) ;
			   }
			   else if (ToFloat(ds_list.GetColumn(i,"ACAPA")) <=  750 ) 
			   {
				 bal =  ( ( 250000 * 7 ) / 10 ) + ( ( ToFloat(ds_list.GetColumn(i,"AFQ"))  - 15 ) * 8000 ) ;
			   }

			 } 
			 // alert(bal);
			  if(ds_list.GetColumn(i,"EQME93") >= bal) {		  
				 ds_list.SetColumn(i,"R_BUDGET", ds_list.GetColumn(i,"R_BUDGET") + bal );
			  } else{
			   ds_list.SetColumn(i,"R_BUDGET", ds_list.GetColumn(i,"R_BUDGET") + ds_list.GetColumn(i,"EQME93") );
			  }
		}



	}




	/*
	//오버홀 예산 수정
    var bal = 0;
    for( var i=0; i<ds_list.rowcount; i++ )
	{ 
		bal = 0;	
		if(ToFloat(ds_list.GetColumn(i,"EQME93")) > 0 )
		{		
		  if (ToFloat(ds_list.GetColumn(i,"ACAPA")) >= 1350 ) 
		   { //alert("a");
		     bal = ( 250000 * 1 ) + ( ( ToFloat(ds_list.GetColumn(i,"AFQ")) - 15 ) * 8000 ) ;
		   }
