/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	//alert(ds_autal.GetColumn(0,"CNT"));

 var clientIp = ext_GetIPAddress();

 //IP대역확인
	clientIp = replace(clientIp, "[" , "");
	clientIp = replace(clientIp, "]" , "");
    clientIp = replace(clientIp, "." , "");
	// IP 설정 

    //alert(substr(ds_cond.GetColumn(0, "CLIENT_IP"),0,4));

    //10.100.지방 , 10.51.  서울 ,  10.168.  이천
     if(substr(clientIp,0,4) == '1051' or substr(clientIp,0,5) == '10100' or substr(clientIp,0,5) == '10168' or ds_autal.GetColumn(0,"CNT") == 1 )
    //  if(ds_autal.GetColumn(0,"CNT") == 1 )
     {
     //  alert("test");
     //  fn_close();
	  // return; 
     }
     else{
     /* 2020-11-03 고범석 책임님 요청으로 (Remote work로 인한 ip 체크 제거)
      gfn_alert("호기별 예산 대 실적은 현대엘리베이터 보안 정보로"
                     + "\n"
                     + "\n사내 망 내부에서만 사용 가능합니다.");          

      gfn_alert("대형 건설사 상주 근무자 경우,"
                     + "\n"
                     + "\n설치관리팀에 문의 주시면 별도 조치 후 사용 가능합니다."
                     + "\n"
                     + "\n(담당자: 고범석 과장)");  
       fn_close();
	   return; */

    }

	if(userGroup == "40")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "30")
	{
		ds_zzactss.Filter("CODE="+quote(G_AREA_CODE_E));
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
	else if(userGroup == "10")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
}
