/********************************************************************************
* 기      능   : 그리드 변경(고재비유무)
********************************************************************************/
function chk_so2_OnClick(obj,strValue)
{  
  if(rd_gbn.Index == 0)
  {
	if(chk_go.Value == 0 )
	{
	   grd_list.Format = "Default";
	}
	else{
  	   grd_list.Format = "Default1";
	}

   }
   else{
       grd_list.Format = "Default3";
   }

	fn_query();
}
/********************************************************************************
* 기      능   : 그리드 변경(구분값 변경)
********************************************************************************/
function rd_gbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(rd_gbn.Index == 0)
	{
      if(chk_go.Value == 0){
  	     grd_list.Format = "Default";}
 	  else{
	     grd_list.Format = "Default1";}
	}
	else{ 
   	      grd_list.Format = "Default3";
	}

}
