var userGroup = "";
var str = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	

	userGroup = gfn_getUserGroup();

	rd_chk.Value = "2";

	//alert("XXX");
	//alert(ds_cond.GetColumn(0,"CHK"));

	rd_chk_OnChanged(rd_chk);

	if(userGroup == '10')
	{
		chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;
	}
	else
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;
	}

	//기준일: 현재월의 첫날짜, 현재날짜
	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-10,1);
	cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_montht.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	cb_gbn.Index = 0;

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps04:PS0401002A_S1");
	tit_addSearchActionInfo("ps02:PS0201001A_S34");

	tit_callService(
	  ""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_lifnr=ds_zzlifnr_cd"
		, ""
		, "fn_afterQueryC"
		, true,true);
	cb_yearf.SetFocus();
}
/********************************************************************************
* 기      능   : 세금계산년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		ds_yeart_cd.AddRow();
		ds_yeart_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yeart_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_yearf.index = eNum;
	cb_yeart.index = eNum;
}
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	if(length(trim(ed_pjt.Text)) == 6) ds_cond.SetColumn(0,"GUBUN","B");
	else ds_cond.SetColumn(0,"GUBUN","A");

	if(ds_cond.GetColumn(0,"GUBUN") == "A")
	{
		var st_date = "";
	if(ds_cond.GetColumn(0,"GBN") == "1") st_date = "세금계산일자";
		else if  (ds_cond.GetColumn(0,"GBN") == "2") st_date = "작업지시일자";
		else st_date = "전표생성일자";
		if(gfn_isNull(cb_yearf, true, st_date)) return;
		if(gfn_isNull(cb_monthf, true, st_date)) return;
		if(gfn_isNull(cb_yeart, true, st_date)) return;
		if(gfn_isNull(cb_montht, true, st_date)) return;	
	}

	var fYyMm  = cb_yearf.Value+""+cb_monthf.Value;
	var tYyMm  = cb_yeart.Value+""+cb_montht.Value;

	if(fYyMm > tYyMm)
	{
		gfn_showMsg("CW00009");
		cb_yeart.SetFocus();
		return;
	}   

	ds_cond.SetColumn(0,"DATEF",fYyMm+"01");
	ds_cond.SetColumn(0,"DATET",tYyMm+"31");

	//조회기간: 30일 이상 조회 불가
//	if(ds_cond.GetColumn(0,"GUBUN") == "A" && gfn_getDayCnt(ds_days,ds_cond.GetColumn(0,"DATEF"), ds_cond.GetColumn(0,"DATET")) > 30)
//	{
//		gfn_showMsg("CW00078"," 1 ");
//		cal_datet.SetFocus();
//		return;
//	}

	var chk = chk_input.value+chk_finish.value+chk_finish2.value;
	if(chk == '000') 
		ds_cond.SetColumn(0,"CHK_GBN","A");
	else 
		ds_cond.SetColumn(0,"CHK_GBN","B");


	//협력업체는 소모품계정 조회 되지 않게 하기 위함
	ds_cond.SetColumn(0,"USERGROUP", userGroup);


	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps04:PS0402001A_S1");  //==> CsvSaerch로 변경
	tit_addCsvSearchActionInfo("ps04:PS0402001A_S1"); 

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {
	if( userGroup == "10")
	{
		ds_lifnr.filter("CODE == " + quote( G_VEND_CODE_E) );
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = false;
	}
	else if ( userGroup == "40" )
	{
		cb_lifnr.Enable = true;
		ds_lifnr.Filter("SO5==''");
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
		cb_lifnr.value = "";
	}	
	else
	{
		cb_lifnr.value = "";
		cb_lifnr.Enable = false;
	}
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	if(userGroup == "40")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "30")
	{
		ds_zzactss.Filter("CODE="+quote(G_AREA_CODE_E));
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
	else if(userGroup == "10")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
}
