/********************************************************************************
* 기      능   : Grid의 '목을 dblClick시, 기타외주작업지시서 상세정보 POP-UP화면으로 연동
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{   
	if(rd_chk.Value == "2") 
	{
		if(userGroup == '40')
		{
			gfn_openPopup("PS0402001B",true);
		}	
	}	
	else if(rd_chk.Value == "1")
	{			//alert(rd_chk.Value);


		var arg = "p_pspid=" + quote(ds_list.getcolumn(ds_list.row, "PSPID")) +
		          " p_gubun=" + quote(ds_list.getcolumn(ds_list.row, "GUBUN")) +
		          " p_hkont=" + quote(ds_list.getcolumn(ds_list.row, "HKONT")) +
		          " p_seqno=" + quote(ds_list.getcolumn(ds_list.row, "SEQNO")) +
		          " p_w_posid=" + quote(ds_list.getcolumn(ds_list.row, "W_POSID")) +
		          " p_w_inst=" + quote(ds_list.getcolumn(ds_list.row, "W_INST")) +
		          " p_change=" + quote("A");

		gfn_openPopup("PS0410001A",true, arg);
	}
}


