/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() 
{

	if ( !gfn_checkFromToObj(cal_zzshipf, cal_zzshipt, true, st_ship.value, 0, true)   ) 
    {	return; 	}




	if (cb_state.Value == 'N')
	{
		ds_cond.SetColumn(0,"STATE2",'');
		ds_cond.SetColumn(0,"GBN2",'A');
	}
	else
	{
		ds_cond.SetColumn(0,"STATE2", ds_cond.GetColumn(0,"STATE"));
		ds_cond.SetColumn(0,"GBN2",'B');
	}
// 안전발팔
   if(rd_gbn.Value == "1" )
   	{  
	   ds_list.ClearData();
	   grd_list.Format = "Default";
       tit_clearActionInfo();
 	   tit_addSearchActionInfo("ps04:PS0404002A_S1");   //==> CsvSaerch로 변경
 	   //tit_addCsvSearchActionInfo("ps04:PS0404002A_S1");  

	}
//디지털통화기기	
   else if(rd_gbn.Value == "2")
   {
   	   ds_list.ClearData();
	   grd_list.Format = "Default1";
       tit_clearActionInfo();
 	   tit_addSearchActionInfo("ps04:PS0404002A_S3");   //==> CsvSaerch로 변경
 	   //tit_addCsvSearchActionInfo("ps04:PS0404002A_S3"); 

   }
   //E/L 철거 
   else if(rd_gbn.Value == "3")
   {
   	   ds_list.ClearData();
	   grd_list.Format = "Default";
       tit_clearActionInfo();
 	   tit_addSearchActionInfo("ps04:PS0404002A_S7");   //==> CsvSaerch로 변경
 	   //tit_addCsvSearchActionInfo("ps04:PS0404002A_S7"); 

   }
   //레일메달기 
   else if(rd_gbn.Value == "4")
   {
   	   ds_list.ClearData();
	   grd_list.Format = "Default";
       tit_clearActionInfo();
 	   tit_addSearchActionInfo("ps04:PS0404002A_S9");   //==> CsvSaerch로 변경
 	   //tit_addCsvSearchActionInfo("ps04:PS0404002A_S9"); 

   }
    //타워크레인 
   else if(rd_gbn.Value == "5")
   {
   	   ds_list.ClearData();
	   grd_list.Format = "Default";
       tit_clearActionInfo();
 	   tit_addSearchActionInfo("ps04:PS0404002A_S10");   //==> CsvSaerch로 변경
 	   //tit_addCsvSearchActionInfo("ps04:PS0404002A_S10"); 

   }
	   tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , ""
		, ""
        , true);


}
/********************************************************************************
