/********************************************************************************
* 기      능   : 신청 
********************************************************************************/
function fn_request() 
{
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	//신청시 지정된 협력업체로 신청이 됨으로 필수 입력되어 있어야 함.
	if(gfn_isNull(cb_lifnr, true, st_lifnr.value)) return;


	for(var i=0; i<ds_list.GetRowCount(); i++){
		if(ds_list.GetColumn(i, "CHK")==1){
			// 필수 체크항목
			//if(length(ds_list.GetColumn(i, "STATE")) > 0)
			//alert(ds_list.GetColumn(i, "STATE"));
          if(ds_list.GetColumn(i, "STATE") <> ' ' or  ds_list.GetColumn(i, "STATE") <> '')
			{	
			//	var arrs = ['신청대상', '신청'];
			//	gfn_showMsg('CW00068', arrs);
			//	return;
			} 

          if(ds_list.GetColumn(i, "ZFDAT") <= 19990101)
	      {
			var arr = [parseInt(i) + 1, "작업완료일"];
			//gfn_showMsg("CW00224", arr);
			gfn_showAlert("CW00224", arr);
			return;
		  }

		 if(rd_gbn.Value == "2") //디지털통화 기기 시 
         {
		  //비상통화장치번호 미입력 및 11자리가 아닐경우  저장불가 
          if(ds_list.GetColumn(i, "DIPBXNUMB") == '00000000000' || ds_list.GetColumn(i, "DIPBXNUMB") == ''  )         
	      {
			var arr = [parseInt(i) + 1, "비상통화장치번호"];
			//gfn_showMsg("CW00224", arr);
			gfn_showAlert("CW00224", arr);
			return;
		  }

	 }
		 else 
			{

			}
		}
	}


	ds_list2.ClearData();
	ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();


	for(var i=0; i<ds_list2.GetRowCount(); i++)
	{   
		ds_list2.SetColumn(i, "LIFNR", cb_lifnr.Value);
	}

    tit_clearActionInfo();		
	//tit_addSearchActionInfo("ps04:PS0404002A_S1");		
	//tit_addMultiActionInfo("ps07:PS0701001A_U1");
	tit_addMultiActionInfo("ps04:PS0404002A_U1");
	tit_callService(
        ""
        , ""
        , "ds_list2=ds_list2"
        , ""        
        , ""
        , "fn_afterSave"
        , ""
		, ""
        , true);


}

/********************************************************************************
* 기      능   : 신청 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");	
		fn_query();	
	}

}
/********************************************************************************
* 기      능   : 취소
********************************************************************************/
function fn_cancel() 
{
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	for(var i=0; i<ds_list.GetRowCount(); i++){
		if(ds_list.GetColumn(i, "CHK")==1){
			// 필수 체크항목
			if(ds_list.GetColumn(i, "STATE") != 'A')
			{				
				gfn_showMsg('CW00053', '신청상태가 않이므로');
				return;
			} 
			else 
			{

			}
		}
	}


	ds_list2.ClearData();
	ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();



	tit_clearActionInfo();		
	//tit_addSearchActionInfo("ps04:PS0404002A_S1");		
	//tit_addMultiActionInfo("ps07:PS0701001A_U1");
	tit_addMultiActionInfo("ps04:PS0404002A_U2");
	tit_callService(
        ""
        , ""
        , "ds_list2=ds_list2"
        , ""        
        , ""
        , "fn_aftercancel"
        , ""
		, ""
        , true);


}

/********************************************************************************
* 기      능   : 취소 Callback  
********************************************************************************/
function fn_aftercancel(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");	
		fn_query();	
	}

}
