/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_list.fireevent = false;
	if(strColumnID!="CHK"){
		ds_list.SetColumn(nRow, "CHK", 1);
	}
	ds_list.fireevent = true;
}

/********************************************************************************
* 기      능   :  구분값 변경시 처리
********************************************************************************/
function cb_state_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == 'N')
	{
		btn_request.enable = true;
		btn_print.enable = false;
	}
	else
	{
		btn_request.enable = false;
		btn_print.enable = true;
	}


	if(strCode == 'A')
	{
		btn_cancel.enable = true;
	}
	else
	{
		btn_cancel.enable = false;
	}	

	if(strCode == 'C')
	{
	  cb_period.Index = 1;
	}
	else
	{
	    cb_period.Index = 0;
	}
}



/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print() 
{

	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}



	ds_list2.ClearData();
	ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

	var arg = "";
	var arg2 = "";
	var param = "";	
	var param2 = "";	
	var pspid = "";

	ds_list2.Sort("PSPID,POSID",True);
	for(var i=0; i<ds_list2.GetRowCount(); i++)
	{		
//alert(	"pspid : " + pspid );	
//alert(	"ds_list2 : " + ds_list2.GetColumn(i,"PSPID"));	
		//if(pspid != "" && pspid != ds_list2.GetColumn(i,"PSPID"))
		if(pspid != ds_list2.GetColumn(i,"PSPID"))
		{	
			arg2 += "'"+ds_list2.GetColumn(i,"PSPID")+"'"+",";		
		}
		pspid = ds_list2.GetColumn(i,"PSPID");


		arg += "'"+ds_list2.GetColumn(i,"POSID")+"'"+",";		
	}

	if(substr(arg2,length(arg2)-1) == ",") arg2 = substr(arg2,0,length(arg2)-1);
	param2 = "  AND A.PSPID IN ("+arg2+")";

	if(substr(arg,length(arg)-1) == ",") arg = substr(arg,0,length(arg)-1);
	param = "  AND A.POSID IN ("+arg+")";


	ds_cond_p.ClearData();
	ds_cond_p.AddRow();	
	ds_cond_p.SetColumn(0,"GUBUN",param);
	ds_cond_p.SetColumn(0,"GUBUN2",param2);
    if(rd_gbn.Value == "1") {  
       ds_cond_p.SetColumn(0,"GBN",'A'); }
	else if(rd_gbn.Value == "2"){
	   ds_cond_p.SetColumn(0,"GBN",'C'); }
    else if(rd_gbn.Value == "3"){
	   ds_cond_p.SetColumn(0,"GBN",'G'); }
    else if(rd_gbn.Value == "4"){
	   ds_cond_p.SetColumn(0,"GBN",'H'); }
    else if(rd_gbn.Value == "5"){
	   ds_cond_p.SetColumn(0,"GBN",'I'); }

//trace(ds_cond_p.SaveXML());        
//alert("xx");

	tit_clearActionInfo();		
	tit_addSearchActionInfo("ps04:PS0404002A_S2");		  //==> CsvSaerch로 변경
	//tit_addCsvSearchActionInfo("ps04:PS0404002A_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond_p=ds_cond_p"
        , "ds_list_p=ds_list_p"
        , ""
        , ""  //"fn_afterQuery"
        , ""
		, ""
        , true);

//trace(ds_list_p.SaveXML());        
//alert("xx");        	
    if(rd_gbn.Value == "1")
    {
	  Dialog("PS04::PS0404002P.xml", "");
	}
	else if(rd_gbn.Value == "2")
	{
	  Dialog("PS04::PS0404002P1.xml", "");
	}
	else if(rd_gbn.Value == "3")//철거업체 
	{
	  Dialog("PS04::PS0404002P2.xml", "");
	}	  
	else if(rd_gbn.Value == "4")//레일메달기업체 
	{
	  Dialog("PS04::PS0404002P3.xml", "");
	}	  
	else if(rd_gbn.Value == "5")//타워크레인 
	{
	  Dialog("PS04::PS0404002P4.xml", "");
	}	
}



function rd_gbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
