#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var userGroup = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	

	userGroup = gfn_getUserGroup();

	if(userGroup == '10')
	{
		chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;
	}
	else
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;
	}

	//기준일: 현재년월
	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-10,1);
	cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;

	ds_cond.SetColumn(0,"USER_GRUP",userGroup);
	if (userGroup == "10")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_USER_ID);
	}
	else if (userGroup == "30")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_AREA_CODE_E);
	}

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	tit_clearActionInfo();
//	tit_addSearchActionInfo("wb01:SEOLCHI_VEND_S2");

	tit_addSearchActionInfo("ps02:PS0201001A_S34");

	tit_callService(
	  ""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_lifnr=ds_zzlifnr_cd"
		, ""
		, "fn_afterQueryC"
		, true,true);

	cb_yearf.SetFocus();

	Radio1.INDEX = 0;
	rd_chk.Index = 0;
    grd_list.Format = "Default";
//ALERT(Radio1.INDEX);	
//ALERT(Radio1.VALUE);	
//ALERT(Radio1.TEXT);
}
/********************************************************************************
* 기      능   : 세금계산년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_yearf.index = eNum;
}
/********************************************************************************
* 기      능   : 라디오 버튼 변경 이베네트
********************************************************************************/
function Radio1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(rd_chk.INDEX == 0)
    {
	   grd_list.Format = "Default";
	}
	else 
	{
	   grd_list.Format = "Default1";
	}

	fn_query();

}

/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	if(gfn_isNull(cb_yearf, true, st_date.value)) return;
	if(gfn_isNull(cb_monthf, true, st_date.value)) return;

	var fYyMm  = cb_yearf.Value+""+cb_monthf.Value;

	ds_cond.SetColumn(0,"DATEF",fYyMm+"01");
	ds_cond.SetColumn(0,"DATET",fYyMm+"31");
	//ds_cond.SetColumn(0,"RD_GBN",rd_gbn);



	//if(length(trim(ed_pjt.Text)) == 6) ds_cond.SetColumn(0,"GBN","A");
	//else ds_cond.SetColumn(0,"GBN","B");


	//조회조건 선택( 생성일자/ 세금계산서일)
	 /*
	if(Radio1.Value == "1")
	{
		ds_cond.SetColumn(0,"RD_GBN","A");
	}
	else
	{ 
		ds_cond.SetColumn(0,"RD_GBN","B");
	}
	*/


	//설치협력사필드 필수체크(설치협력사 로그인시)
	if( userGroup == '10' && gfn_isNull(cb_lifnr, true, st_lifnr.value)) return;

//TRACE(ds_cond.SaveXML());
//ALERT("ZZZZ");
	tit_clearActionInfo();
	//alert(rd_chk.Value);
	//tit_addSearchActionInfo("ps04:PS0405001A_S1");  //==> CsvSaerch로 변경
	if(rd_chk.Value = '1'){
	tit_addCsvSearchActionInfo("ps04:PS0405001A_S1"); } //일반운영비
	else{
	tit_addCsvSearchActionInfo("ps04:PS0405001A_S2"); }  //효율개선비

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {
	cb_lifnr.Index = 0;

	if ( userGroup == "10" )
	{
		ds_lifnr.Filter("CODE=="+quote(G_VEND_CODE_E));
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = false;
	}
	else if ( userGroup == "30" || userGroup == "40" )
	{
		ds_lifnr.Filter("SO5==''");
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
		cb_lifnr.value = "";
	}
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	if(userGroup == "40")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "30")
	{
		ds_zzactss.Filter("CODE="+quote(G_AREA_CODE_E));
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
	else if(userGroup == "10")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
