#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var userGroup = "";
var currRow = "";
var selGbn = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	

	userGroup = gfn_getUserGroup();

	if(userGroup == '10')
	{
		chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;
	}
	else
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;
	}

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-10,1);
	cb_month.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;

	ds_cond.SetColumn(0,"USER_GRUP",userGroup);
	if (userGroup == "10")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_USER_ID);
	}
	else if (userGroup == "30")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_AREA_CODE_E);
	}

	btn_add.Enable = false;
	btn_del.Enable = false;

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	tit_clearActionInfo();
//	tit_addSearchActionInfo("wb01:SEOLCHI_VEND_S2");
    tit_addSearchActionInfo("ps02:PS0201001A_S34");

	tit_callService(
	  ""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_lifnr=ds_zzlifnr_cd"
		, ""
		, "fn_afterQueryC"
		, true,true);

	cb_month.SetFocus();
}
/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_year.AddRow();
		ds_year.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_year.index = eNum;
}
/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	//년도필드 필수체크	
	if(gfn_isNull(cb_year, true, st_yymm.value))
		return;
	//월필드 필수체크	
	if(gfn_isNull(cb_month, true, st_yymm.value))
		return;

	//설치협력사필드 필수체크(설치협력사 로그인시)
	if( userGroup == '10' && gfn_isNull(cb_lifnr, true, st_lifnr.value))
		return;

	ds_cond.SetColumn(0,"YYYYMM",ds_cond.GetColumn(0,"YEAR")+ds_cond.GetColumn(0,"MONTH"));

	tit_clearActionInfo();
	if(length(trim(ed_pjt.Text)) < 6)
	{
		//조회조건에 프로젝트번호가 없는경우 ZPST0133-[WEB] 핀제거완료 테이블에서 기존에 등록한 정보 조회
		selGbn = "A";
		tit_addSearchActionInfo("ps04:PS0407001A_S1");
	}	
	else
	{
		//조회조건에 프로젝트번호가 있는경우 ZPST0133-[WEB] 핀제거완료 테이블에서 기존에 등록한 정보 조회하고
		//없는경우 호기마스터에서 업체에 해당하는 호기정보 조회한다.
		selGbn = "B";
		tit_addSearchActionInfo("ps04:PS0407001A_S4");
	}	
	//tit_addSearchActionInfo("ps04:PS0407001A_S3");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
