/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	ds_cond_back.ClearData();
	ds_cond_back.Copy(ds_cond);

	var yyyymm = ds_cond_back.GetColumn(0,"YEAR")+ds_cond_back.GetColumn(0,"MONTH");
	if(substr(gfn_getCurrDate(),0,6) != yyyymm && (userGroup == '10' || userGroup == '30'))	
		selGbn = "A";

	if(selGbn == "B" && ds_list.rowcount == 0)
	{
		fn_query2();
	}	

/*    입력 막음   */
	//btn_add.Enable = true;
	//btn_del.Enable = true;
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterPosid(errCode, errMsg) {
	if(ds_list2.RowCount() > 0)
	{
		ds_list.SetColumn(currRow,"POSID",ds_list2.GetColumn(0,"POSID"));
		ds_list.SetColumn(currRow,"ZSITE_NM",ds_list2.GetColumn(0,"ZSITE_NM"));
		ds_list.SetColumn(currRow,"ZZACTSS",ds_list2.GetColumn(0,"ZZACTSS"));
		ds_list.SetColumn(currRow,"ZQNTY",ds_list2.GetColumn(0,"ZQNTY"));
		ds_list.SetColumn(currRow,"VALUE",ds_list2.GetColumn(0,"VALUE"));
		ds_list.SetColumn(currRow,"YYYYMM",ds_cond_back.GetColumn(0,"YYYYMM"));
		ds_list.SetColumn(currRow,"LIFNR",ds_cond_back.GetColumn(0,"LIFNR"));
	}
	else
	{
		ds_list.SetColumn(currRow,"POSID","");
		ds_list.SetColumn(currRow,"ZSITE_NM","");
		ds_list.SetColumn(currRow,"ZZACTSS","");
		ds_list.SetColumn(currRow,"ZQNTY","");
		ds_list.SetColumn(currRow,"VALUE","");
		ds_list.SetColumn(currRow,"YYYYMM","");
		ds_list.SetColumn(currRow,"LIFNR","");
	}
}
/********************************************************************************
* 기      능   :  협력사 조회 Callback  
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {

	for(var i=0; i<ds_lifnr.rowcount; i++)
	{
		if( length(ds_lifnr.GetColumn(i,"SO5")) <= 0 )
		{
			ds_lifnr.SetColumn(i,"SO5",'');
		}
	}

	cb_lifnr.Index = 0;

	if ( userGroup == "10" )
	{
		ds_lifnr.Filter("CODE=="+quote(G_VEND_CODE_E));
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = false;
	}
	else if ( userGroup == "30" || userGroup == "40")
	{
		ds_lifnr.Filter("SO5==''");
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
		gfn_insertEmptyRow( ds_zzactss, G_CODE_ALL );
		cb_lifnr.value = "";
		cb_lifnr.Index = 0;
	}
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if ( !gfn_isChangeDs(ds_list, true, "FLAG") ) {
		return;
	}

	for(var i=0; i<ds_list.rowcount; i++)
	{
		if(ds_list.GetColumn(i,"FLAG") == 'I' && length(trim(ds_list.GetColumn(i,"POSID_1"))) < 9)
		{
			gfn_showMsg("CW00038",st_posid.Value); 
			return;
		}	
		if(length(trim(ds_list.GetColumn(i,"VALUE"))) < 1)
		{
			gfn_showMsg("CW00001",st_value.Value); 
			return;
		}	
		if(length(trim(ds_list.GetColumn(i,"XVALUE"))) < 1)
		{
			gfn_showMsg("CW00001",st_xvalue.Value); 
			return;
		}	

	}	

	tit_clearActionInfo();
	tit_addSaveActionInfo(
        "ps04:PS0407001A_I1"
        , "ps04:PS0407001A_U1"
        , "ps04:PS0407001A_D1"
        , "FLAG"
        , ""
        , -1
        , "");

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A"
        , ""
        , ""
        , "fn_afterSave");
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}
