}

/********************************************************************************
* 기      능   : 사업부 선택시 ds_lifnr 데이터셋 값 filtering
********************************************************************************/
function chk_so2_OnClick(obj,strValue)
{
	var so = ' ';
	var so2 = ' ';
	var so3 = ' ';
	var so4 = ' ';
	var so5 = ' ';
	var so7 = ' ';

	if(chk_so.Value == '1')  so =  'X';
	if(chk_so2.Value == '1') so2 = 'X';
	if(chk_so3.Value == '1') so3 = 'X';
	if(chk_so4.Value == '1') so4 = 'X';
	if(chk_so5.Value == '1') so5 = 'X';
	if(chk_so7.Value == '1') so7 = 'X';


	if(chk_so.Value+chk_so2.Value+chk_so3.Value+chk_so4.Value+chk_so5.Value+chk_so7.Value == '000000')
	{
		ds_lifnr.Filter("SO5=='' || CODE==''");
	}	
	else
	{
	  ds_lifnr.Filter("CODE=='' || SO=="+quote(so) +" || SO2=="+quote(so2) +" || SO3=="+quote(so3) +" || SO4=="+quote(so4) +" || SO5=="+quote(so5) +" || SO7=="+quote(so7));
	}

	cb_lifnr.Index = 0;	
}
/********************************************************************************
* 기      능   : 삽입 20
********************************************************************************/
function fn_insert() {
	var yyyymm = ds_cond_back.GetColumn(0,"YEAR")+ds_cond_back.GetColumn(0,"MONTH");

	if(substr(gfn_getCurrDate(),0,6) != yyyymm && (userGroup == '10' || userGroup == '30'))
		return;

	ds_list.fireevent = false;
	ds_list.AddRow();			
	ds_list.SetColumn(ds_list.currow,"FLAG","I");
	ds_list.fireevent = true;
}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
  //if(ds_list.GetColumn(ds_list.row,"FLAG") == "")
	if(ds_list.GetColumn(ds_list.row,"FLAG") == "" || length(ds_list.GetColumn(ds_list.row,"FLAG")) <= 0    )
	{
		if(substr(gfn_getCurrDate(),0,6) == ds_list.GetColumn(ds_list.row,"YYYYMM") || userGroup == '40')
			ds_list.SetColumn(ds_list.row,"FLAG","D");
	}
	else if(ds_list.GetColumn(ds_list.row,"FLAG") == "D")
		ds_list.SetColumn(ds_list.row,"FLAG","");
	else
		ds_list.DeleteRow(ds_list.row);
}
/********************************************************************************
* 기      능   : cell이동시 프로젝트정보 자동검색
********************************************************************************/
function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if(nOldCell = 3 && length(ds_list.GetColumn(nOldRow,"POSID_1")) >0 && ds_list.GetColumn(nOldRow,"FLAG") == "I")
	{
		currRow = nOldRow;
		fn_posid(ds_list.GetColumn(nOldRow,"POSID_1"),cb_lifnr.Value);
	}	
}
/********************************************************************************
* 기      능   : cell에 텍스트입력시 프로젝트정보 자동검색
********************************************************************************/
function grd_list_OnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam,nRow,nCell,nPivotIndex)
{
	if(length(strPostText) > 8 && nCell = 3 && ds_list.GetColumn(nRow,"FLAG") == "I")
	{
		currRow = nRow;
		fn_posid(trim(strPostText),cb_lifnr.Value);
	}
}
/********************************************************************************
* 기      능   : 그리드에서 포커스이동시 프로젝트정보 자동검색
********************************************************************************/
function grd_list_OnKillFocus(obj)
{
	currRow = ds_list.row;
	fn_posid(ds_list.GetColumn(ds_list.row,"POSID"),cb_lifnr.Value);
}
/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print() 
{
	if ( gfn_isChangeDs(ds_list, false, "FLAG") ) {

		gfn_showMsg("CW00202");
		return;
	}
	if(ds_list.RowCount() == 0)
	{
		gfn_showMsg("CW00010");
		return;
	}	

	// UbiReport Call
	Dialog("PS04::PS0407001P.xml", "");
}
/********************************************************************************
* 기      능   : 비고 edit여부 제어
********************************************************************************/
function fn_chgEdit(currow)
{
	if(userGroup == "40" || ds_list.GetColumn(currow,"FLAG") == "I") return "normal";
	else return "none";
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "BIGO":
			if(ds_list.GetColumn(nRow,"FLAG") != "I")
				ds_list.SetColumn(ds_list.currow,"FLAG","U");
			break;
	}
	obj.fireevent = true;

}


/********************************************************************************
* 기      능   : 사무소 별 협력업체 필터 
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
  if(userGroup == "40"){
    ds_cond.SetColumn(0,"LIFNR",''); }

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_lifnr.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_lifnr.Filter("ZZACTSS ==" +quote(arr));
    }
}
