}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	//년도필드 필수체크	
	if(gfn_isNull(cb_year, true, st_yymm.value))
		return;
	//월필드 필수체크	
	if(gfn_isNull(cb_month, true, st_yymm.value))
		return;

	//설치협력사필드 필수체크(설치협력사 로그인시)
	if( userGroup == '10' && gfn_isNull(cb_lifnr, true, st_lifnr.value))
		return;

	ds_cond.SetColumn(0,"YYYYMM",ds_cond.GetColumn(0,"YEAR")+ds_cond.GetColumn(0,"MONTH"));

	tit_clearActionInfo();
	if(length(trim(ed_pjt.Text)) == 6)
	{
		selGbn = "B";
		//tit_addSearchActionInfo("ps04:PS0407002A_S6");  //==> CsvSaerch로 변경
		tit_addCsvSearchActionInfo("ps04:PS0407002A_S6"); 

	}
	else
	{
		var arr = ["프로젝트","6"];
		gfn_showMsg("CW00174",arr);
		return;
	}

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	ds_cond_back.ClearData();
	ds_cond_back.Copy(ds_cond);

	var yyyymm = ds_cond_back.GetColumn(0,"YEAR")+ds_cond_back.GetColumn(0,"MONTH");
	if(substr(gfn_getCurrDate(),0,6) != yyyymm && (userGroup == '10' || userGroup == '30'))	
		selGbn = "A";

	if(selGbn == "B" && ds_list.rowcount == 0)
	{
		fn_query2();
	}	

	btn_add.Enable = true;
	btn_del.Enable = true;
}


