/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterPosid(errCode, errMsg) {
	if(ds_list2.RowCount() > 0)
	{
		ds_list.SetColumn(currRow,"PSPID",ds_list2.GetColumn(0,"PSPID"));
		ds_list.SetColumn(currRow,"ZSITE_NM",ds_list2.GetColumn(0,"ZSITE_NM"));
		ds_list.SetColumn(currRow,"ZZACTSS",ds_list2.GetColumn(0,"ZZACTSS"));
		ds_list.SetColumn(currRow,"ZQNTY",ds_list2.GetColumn(0,"ZQNTY"));
		ds_list.SetColumn(currRow,"VALUE",ds_list2.GetColumn(0,"VALUE"));
		ds_list.SetColumn(currRow,"ZSPEC2",ds_list2.GetColumn(0,"ZSPEC2"));
		ds_list.SetColumn(currRow,"XVALUE",ds_list2.GetColumn(0,"XVALUE"));

		ds_list.SetColumn(currRow,"YYYYMM",ds_cond_back.GetColumn(0,"YYYYMM"));
		ds_list.SetColumn(currRow,"LIFNR",ds_list2.GetColumn(0,"LIFNR"));
		ds_list.SetColumn(currRow,"NAME1",ds_list2.GetColumn(0,"NAME1"));

		ds_list.SetColumn(currRow,"SY_STAT",ds_list2.GetColumn(0,"SY_STAT"));

		ds_list.SetColumn(currRow,"BIGO2",ds_list2.GetColumn(0,"BIGO2"));

	    fn_afterPosid2();	
	}
	else
	{
		ds_list.SetColumn(currRow,"PSPID","");
		ds_list.SetColumn(currRow,"ZSITE_NM","");
		ds_list.SetColumn(currRow,"ZZACTSS","");
		ds_list.SetColumn(currRow,"ZQNTY","");
		ds_list.SetColumn(currRow,"VALUE","");
		ds_list.SetColumn(currRow,"ZSPEC2","");
		ds_list.SetColumn(currRow,"XVALUE","");

		ds_list.SetColumn(currRow,"YYYYMM","");
		ds_list.SetColumn(currRow,"LIFNR","");
		ds_list.SetColumn(currRow,"NAME1","");

	    ds_list.SetColumn(currRow,"CHK","");
        ds_list.SetColumn(currRow,"CHK2","");
	}
}


