/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if ( !gfn_isChangeDs(ds_list, true, "FLAG") ) {
		return;
	}

	for(var i=0; i<ds_list.rowcount; i++)
	{
		if(ds_list.GetColumn(i,"FLAG") == 'I' && length(trim(ds_list.GetColumn(i,"POSID"))) < 9)
		{
			gfn_showMsg("CW00038",st_posid.Value); 
			return;
		}	
		if(length(trim(ds_list.GetColumn(i,"VALUE"))) < 1)
		{
			gfn_showMsg("CW00001",st_value.Value); 
			return;
		}	
		if(length(trim(ds_list.GetColumn(i,"XVALUE"))) < 1)
		{
			gfn_showMsg("CW00001",st_xvalue.Value); 
			return;
		}	

	}

	for(var i=0; i<ds_list2.rowcount; i++)
	{
	//alert(ds_list2.GetColumn(i,"CHK"));
	//alert(ds_list2.GetColumn(i,"CHK2"));

        if(ds_list2.GetColumn(i,"CHK") <> 'Y' && ds_list2.GetColumn(i,"CHK2") <> 'Y')
		{
			gfn_showAlert("CW10025","핀제거 대상현장이 아닙니다.(콘크리트 골조가 아닙니다 / 영업사양미반영)"); 
			return;
		}
  	    else if(ds_list2.GetColumn(i,"CHK") = 'Y' && ds_list2.GetColumn(i,"CHK2") <> 'Y')
		{
			gfn_showAlert("CW10025","핀제거 대상현장이 아닙니다.(콘크리트 골조가 아닙니다 / 영업사양미반영)"); 
			return;
		}
	    else if(ds_list2.GetColumn(i,"CHK") <> 'Y' && ds_list2.GetColumn(i,"CHK2") = 'Y')
		{
			gfn_showAlert("CW10025","핀제거 대상현장이 아닙니다.(콘크리트 골조가 아닙니다 / 영업사양미반영)"); 
			return;
		}	
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
          "ps04:PS0407002A_I1"
        , "ps04:PS0407002A_U1"
        , "ps04:PS0407002A_D1"
        , "FLAG"
        , ""
        , -1
        , "");

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A"
        , ""
        , ""
        , "fn_afterSave");
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}
