/********************************************************************************
* 기      능   : 조회(COUNT)
********************************************************************************/
function fn_query() {
	//필수체크	
	if(gfn_isNull(ed_pjt, true, st_pjt.value))
		return;

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps04:PS0408001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_cnt=ds_cnt"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
