********************************************************************************/
function fn_save() {
//	if ( !gfn_isChangeDs(ds_list, true, "") ) 
//	{
//		return;
//	}
	if(ds_list.GetColumn((ds_list.lastrow),"SCONTAMT") == "0"
	  || ds_list.GetColumn((ds_list.lastrow),"SCONTWAMT") == "0") {
		gfn_showAlert("CW00022","하도급금액");
		return;
	}
	if (ds_list.RowCount() == 0) {
		gfn_showAlert("CW00010");
		return;
	}

	tit_clearActionInfo();
	tit_addSelectExActionInfo("ps04:PS0408001A_S4", "ps04:PS0408001A_I1", "ps04:PS0408001A_U1");

	tit_callService(
		""
		, ""
		, "ds_list=ds_list"
		, ""
		, ""
		, "fn_afterSave");
}
/********************************************************************************
* 기      능   : 저장 - 프린트여부
********************************************************************************/
function fn_save2() {


	ds_list_p.ClearData();
	ds_list_p.AddRow();
	//선택한 row출력
	//ds_list_p.copyrow(0,"ds_list",ds_list.row);
	//마지막 row만 출력
	ds_list_p.copyrow(0,"ds_list",(ds_list.rowcount - 1));
	//착공일 - 10세팅
	ds_list_p.SetColumn(0,"CHAGDT2",gfn_getBeforeDate(ds_list_p.GetColumn(0,"CHAGDT"),10));

	tit_clearActionInfo();
	tit_addMultiActionInfo("ps04:PS0408001A_U2");
	tit_addSearchActionInfo("ps04:PS0408001A_S5");
	tit_callService(
		""
		, ""		
		, "ds_list_p=ds_list_p"
		, "ds_list_p2=ds_list_p2"
		, ""
		, "fn_afterSave2"
		, true);


}
