/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	ds_cond_copy.copy(ds_cond);

	if(errCode == 0)
	{
		tit_clearActionInfo();
		if(ds_cnt.GetColumn(0,"CNT") > 0)
			tit_addSearchActionInfo("ps04:PS0408001A_S2");
		else
			tit_addSearchActionInfo("ps04:PS0408001A_S3");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery2"
			, true
			, true);
	}		

}
