********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
		fn_query();
	}
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave2(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		

		 if(med_scontamt.Value>0)
	{

	  var han = gfn_changeStringNum(replace(med_scontamt.Text,',',''),0);
	  ed_netwr.Text = han; 
	  ds_list_p2.SetColumn(0, "HHADO", han);

	  var vat = FLOOR(ds_list.GetColumn(ds_list.row,"SCONTAMT") / 10) ;
	  ed_vat.Text = vat;
	  ds_list_p2.SetColumn(0, "VAT2", vat);

	   var han2 = gfn_changeStringNum(replace(ed_vat.Text,',',''),0);
	   ed_hvat2.Text = han2; 
	   ds_list_p2.SetColumn(0, "HVAT3", han2);

       var total = ds_list.GetColumn(ds_list.row,"SCONTAMT") + vat ;
       ed_total2.Text = total;
       ds_list_p2.SetColumn(0, "TOTAL", total);

       var han3 = gfn_changeStringNum(replace(ed_total2.Text,',',''),0);
	   ed_htotal2.Text = han3; 
	   ds_list_p2.SetColumn(0, "HTOTAL2", han3);

	    var han4 = gfn_changeStringNum(replace(ed_labc.Text,',',''),0);
	    ed_hlab.Text = han4 ; 
	    ds_list_p2.SetColumn(0, "HLAB_C", han4);

     }

//alert(ds_list_p2.GetColumn(0, "VAT2"));
//trace(ds_list_p2.SaveXML());
		// UbiReport Call
		gfn_openPopup("PS0408001C",true);
		fn_query();
	}
}
