function fn_copy()
{
	if(ds_list.rowcount > 0 && ds_list.GetColumn((ds_list.rowcount - 1),"PRTGBN") == "Y" )
	{
		ds_list.AddRow();
		ds_list.copyrow((ds_list.rowcount - 1),"ds_list",(ds_list.rowcount - 2));
		ds_list.SetColumn((ds_list.rowcount - 1),"PRTGBN","");
		ds_list.SetColumn((ds_list.rowcount - 1),"SEQ","");

		chg_cell("");
	}
}
/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print()
{  
    //trace(ds_list.SaveXML());
   /* ds_list_p2.SetColumn(0,"ZSITE_NM",ds_list.GetColumn(0,"ZSITE_NM"));
    ds_list_p2.SetColumn(0,"HHADO",ds_list.GetColumn(0,"HHADO"));
    ds_list_p2.SetColumn(0,"VAT2",ds_list.GetColumn(0,"VAT2"));
    ds_list_p2.SetColumn(0,"HVAT2",ds_list.GetColumn(0,"HVAT2"));
    ds_list_p2.SetColumn(0,"TOTAL2",ds_list.GetColumn(0,"TOTAL2"));
    ds_list_p2.SetColumn(0,"HTOTAL2",ds_list.GetColumn(0,"HTOTAL2"));
    alert(ds_list.GetColumn(0,"HTOTAL2"));
    alert(ds_list_p2.GetColumn(0,"HTOTAL2"));*/
	//발행버튼 없애고, 출력버튼으로 구분 출력
	if(ds_list.GetColumn((ds_list.lastrow),"SCONTAMT") == "0"
	  || ds_list.GetColumn((ds_list.lastrow),"SCONTWAMT") == "0")
		fn_out();
	else {
		if (ds_list.RowCount() == 0) {
			gfn_showAlert("CW00010");
			return;
		}

		if (  gfn_isChangeDs(ds_list, false, "") || ds_list.GetColumn((ds_list.rowcount -1),"SITE_REP") == '' || ds_list.GetColumn((ds_list.rowcount -1),"SAVE_GBN") == 'N') {
			gfn_showMsg("CW00202");
			return;
		}
	//gfn_isChangeDs(ds_list, false, "") ||
		fn_save2();

	}
}
