/********************************************************************************
* 기      능   : 실지급액 = (배당금-원천세)*주식수
********************************************************************************/
function med_amt_OnChanged(obj,strText)
{
	med_amt3.Value = (ParseInt(med_amt1.Value) - ParseInt(med_amt2.Value))*ParseInt(med_qty.Value);

	if(med_amt3.Value > 0) chk_mflag.Value = '1';
	else chk_mflag.Value = '0';
}
/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
