/********************************************************************************
* 기      능   : 숫자금액을 한글금액으로 변환
********************************************************************************/
function med_contamt_OnCharChanged(obj,strPreText,strPostText)
{
	var han = gfn_changeStringNum(replace(strPostText,',',''),0);
	ed_conthamt.Text = han;
}
function med_scontamt_OnCharChanged(obj,strPreText,strPostText)
{
	//하도급예정급액이 > 0 일 경우 하도급율 계산하기
	if(ds_list.GetColumn(ds_list.row,"SCONTWAMT") > 0)
	{
		rate = round(ParseFloat(replace(strPostText,',',''))/ParseFloat(ds_list.GetColumn(ds_list.row,"SCONTWAMT"))*100,2);
	    //ed_vat.Text = vat;
		}
	else
	{
		ds_list.SetColumn(ds_list.row,"RATE","0.00");
	}	


	var han = gfn_changeStringNum(replace(strPostText,',',''),0);
	ed_sconthamt.Text = han;
	ed_netwr.Text = han; 
}
function med_scontwamt_OnCharChanged(obj,strPreText,strPostText)
{
	//하도급예정급액이 > 0 일 경우 하도급율 계산하기
	if(replace(strPostText,',','') > 0)
	{
		rate = round(ParseFloat(ds_list.GetColumn(ds_list.row,"SCONTAMT"))/ParseFloat(replace(strPostText,',',''))*100,2);
		ds_list.SetColumn(ds_list.row,"RATE",rate);



	}
	else
	{
		ds_list.SetColumn(ds_list.row,"RATE","0.00");

	}	

	var han = gfn_changeStringNum(replace(strPostText,',',''),0);
	ed_scontwhamt.Text = han;
}
function med_supamt_OnCharChanged(obj,strPreText,strPostText)
{
	var han = gfn_changeStringNum(replace(strPostText,',',''),0);
	ed_suphamt.Text = han;
}
function med_sigongamt_OnCharChanged(obj,strPreText,strPostText)
{
	var han = gfn_changeStringNum(replace(strPostText,',',''),0);
	ed_sigonghamt.Text = han;
}
function med_vat_OnCharChanged(obj,strPreText,strPostText)
{
	var han = gfn_changeStringNum(replace(strPostText,',',''),0);
	ed_hvat.Text = han; 
}
